import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PaymentPage = ({ userId, medicines, totalAmount }) => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const handlePlaceOrder = async () => {
        try {
            setIsLoading(true);
            const response = await axios.post(
                '//orders/',  // The API endpoint for placing an order
                {
                    user_id: userId,
                    medicines: medicines,
                    total_amount: totalAmount,
                    payment_type: 'COD',  // Hardcoding COD for this example
                },
                {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('authToken')}`,  // Assuming JWT for authentication
                    },
                }
            );

            console.log(response.data);  // Handle success
            alert('Order placed successfully!');
        } catch (err) {
            console.error(err);
            setError('Something went wrong. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div>
            <h1>Payment Page</h1>
            <p>Payment Method: Cash on Delivery (COD)</p>
            <p>Total Amount: ${totalAmount}</p>
            
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <button onClick={handlePlaceOrder} disabled={isLoading}>
                {isLoading ? 'Placing Order...' : 'Place Order'}
            </button>
        </div>
    );
};

export default PaymentPage;
