const styles = {
  container: {
    padding: "20px",
    fontFamily: "'Roboto', sans-serif",
    backgroundColor: "#f9f9f9",
    minHeight: "100vh",
  },
  backButton: {
    backgroundColor: "#007BFF",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
    marginBottom: "20px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    transition: "background-color 0.3s ease",
  },
  heading: {
    textAlign: "center",
    fontSize: "2.5rem",
    fontWeight: "bold",
    color: "#333",
    marginBottom: "30px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    backgroundColor: "white",
    borderRadius: "8px",
    overflow: "hidden",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
  },
  tableHeader: {
    backgroundColor: "#007BFF",
    color: "white",
    padding: "15px",
    textAlign: "left",
    fontWeight: "bold",
    fontSize: "1rem",
  },
  tableRow: {
    backgroundColor: "#ffffff",
    textAlign: "left",
    fontSize: "0.9rem",
    borderBottom: "1px solid #ddd",
  },
  tableCell: {
    padding: "15px",
    color: "#333",
  },
  statusSelect: {
    padding: "10px",
    borderRadius: "5px",
    fontSize: "1rem",
    cursor: "pointer",
    border: "1px solid #ddd",
    backgroundColor: "#f4f4f4",
    transition: "background-color 0.3s ease",
  },
};

const getStatusStyle = (status) => {
  switch (status) {
    case "confirmed":
      return {
        color: "white",
        backgroundColor: "#28a745",
        padding: "5px 10px",
        borderRadius: "5px",
        fontWeight: "bold",
      };
    case "pending":
      return {
        color: "white",
        backgroundColor: "#ffc107",
        padding: "5px 10px",
        borderRadius: "5px",
        fontWeight: "bold",
      };
    case "rejected":
      return {
        color: "white",
        backgroundColor: "#dc3545",
        padding: "5px 10px",
        borderRadius: "5px",
        fontWeight: "bold",
      };
    default:
      return {
        color: "white",
        backgroundColor: "#6c757d",
        padding: "5px 10px",
        borderRadius: "5px",
        fontWeight: "bold",
      };
  }
};
