import React, { useEffect, useState, useCallback, useMemo } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();

  const user_data = useMemo(
    () => ({ user_id: sessionStorage.getItem("user_id") }),
    []
  );

  const fetchCart = useCallback(async () => {
    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/cart/",
        user_data
      );
      setCartItems(response.data.cart_items);
      setTotalPrice(response.data.total_price);
    } catch (error) {
      console.error("Error fetching cart items:", error);
    }
  }, [user_data]);

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  const handleRemoveFromCart = async (cartId) => {
    try {
      await axios.post(
        `http://localhost:8000/medical_store_app/cart/remove/${cartId}/${sessionStorage.getItem(
          "user_id"
        )}`
      );
      fetchCart();
    } catch (error) {
      console.error("Error removing cart item:", error);
    }
  };

  const handleProceedToCheckout = async () => {
    try {
      // Fetch Razorpay order ID from the backend
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/create_order/",
        {
          user_id: sessionStorage.getItem("user_id"),
          amount: totalPrice, // Assuming `totalPrice` is in INR
        }
      );

      const { order_id } = response.data;

      const options = {
        key: "your_public_key", // Replace with your Razorpay public key
        amount: totalPrice * 100, // Amount in paise
        currency: "INR",
        name: "Medical Store",
        description: "Order Payment",
        order_id: order_id, // Razorpay order ID from backend
        handler: async (response) => {
          console.log("Payment successful:", response);
          // Post-payment success handling
          await axios.post("http://localhost:8000/medical_store_app/payment_success/", {
            user_id: sessionStorage.getItem("user_id"),
            razorpay_payment_id: response.razorpay_payment_id,
            razorpay_order_id: response.razorpay_order_id,
            razorpay_signature: response.razorpay_signature,
          });
          navigate("/Booking", { state: { order_id } });
        },
        prefill: {
          name: "Your Name", // Optional: prefill name
          email: "your.email@example.com", // Optional: prefill email
          contact: "1234567890", // Optional: prefill phone
        },
        theme: {
          color: "#3399cc",
        },
      };

      const razorpay = new Razorpay(options);
      razorpay.open();
    } catch (error) {
      console.error("Error during payment initiation:", error);
    }
  };

  const handleContinueShopping = () => {
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      <button
        style={styles.continueShoppingButton}
        onClick={handleContinueShopping}
      >
        Continue Shopping
      </button>
      <h1 style={styles.heading}>Your Cart</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td>{item.medicine.name}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  onClick={() => handleRemoveFromCart(item.id)}
                  style={styles.button}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 style={styles.total}>Total: â‚¹{totalPrice}</h3>
      <div style={styles.BookContainer}>
        <button style={styles.paymentButton} onClick={handleProceedToCheckout}>
          Proceed to Payment
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: "70px",
    fontFamily: "Arial, sans-serif",
  },
  continueShoppingButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  tableHeader: {
    backgroundColor: "#f4f4f4",
    padding: "10px",
    textAlign: "left",
    fontWeight: "bold",
  },
  button: {
    backgroundColor: "#ff4d4d",
    color: "white",
    border: "none",
    padding: "5px 10px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  total: {
    fontSize: "1.5rem",
    textAlign: "right",
    marginTop: "20px",
    fontWeight: "bold",
  },
  BookContainer: {
    display: "flex",
    justifyContent: "center",
    marginTop: "20px",
  },
  paymentButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Cart;
