// Cart.js
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const API_BASE_URL = "http://localhost:8000/medical_store_app/";

export default function Cart() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  // Fetch cart items from API
  useEffect(() => {
    axios.get(`${API_BASE_URL}/cart/`)
      .then((response) => setCart(response.data))
      .catch((error) => console.error("Error fetching cart items:", error));
  }, []);

  // Remove a medicine from the cart via API
  const removeFromCart = (id) => {
    axios.delete(`${API_BASE_URL}/cart/${id}/`)
      .then(() => {
        const updatedCart = cart.filter((medicine) => medicine.id !== id);
        setCart(updatedCart);
      })
      .catch((error) => console.error("Error removing item from cart:", error));
  };

  // Calculate total price
  const totalPrice = cart.reduce((total, medicine) => {
    const price = parseFloat(medicine.price) || 0; // Convert to number, default to 0 if invalid
    return total + price * medicine.quantity;
  }, 0);

  return (
    <div style={styles.cartPage}>
      <h2 style={styles.cartTitle}>Your Cart</h2>
      {cart.length === 0 ? (
        <p style={styles.emptyCart}>
          Your cart is empty. <Link to="/">Continue shopping</Link>
        </p>
      ) : (
        <>
          <ul style={styles.cartList}>
            {cart.map((medicine) => {
              const price = parseFloat(medicine.price) || 0; // Convert to number, default to 0 if invalid
              return (
                <li key={medicine.id} style={styles.cartItem}>
                  <h3 style={styles.medicineName}>{medicine.medicine.name}</h3>
                  <p style={styles.price}>Price: ${price.toFixed(2)}</p>
                  <p style={styles.quantity}>Quantity: {medicine.quantity}</p>
                  <button 
                    onClick={() => removeFromCart(medicine.id)} 
                    style={styles.removeButton}>
                    Remove
                  </button>
                </li>
              );
            })}
          </ul>
          <div style={styles.cartTotal}>
            <h3>Total: ${totalPrice.toFixed(2)}</h3>
            <button 
              style={styles.checkoutButton} 
              onClick={() => navigate("/checkout")}>
              Proceed to Checkout
            </button>
          </div>
        </>
      )}
    </div>
  );
}

// Inline CSS styles
const styles = {
  cartPage: {
    maxWidth: "600px",
    margin: "2em auto",
    padding: "1em",
    background: "#fff",
    borderRadius: "8px",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
  },
  cartTitle: {
    fontSize: "24px",
    marginBottom: "1em",
    textAlign: "center",
  },
  emptyCart: {
    textAlign: "center",
    fontSize: "16px",
  },
  cartList: {
    listStyle: "none",
    padding: "0",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "0.5em 0",
    borderBottom: "1px solid #ddd",
  },
  medicineName: {
    fontSize: "18px",
  },
  price: {
    fontSize: "16px",
  },
  quantity: {
    fontSize: "16px",
  },
  removeButton: {
    padding: "5px 10px",
    color: "#fff",
    backgroundColor: "#e74c3c",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  cartTotal: {
    marginTop: "1em",
    fontSize: "18px",
    textAlign: "right",
  },
  checkoutButton: {
    padding: "10px 20px",
    color: "#fff",
    backgroundColor: "#2ecc71",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
};
