import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const [paymentMethod, setPaymentMethod] = useState("COD");
  const [address, setAddress] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [loading, setLoading] = useState(false);
  const [userAddress, setUserAddress] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchUserAddress();
    fetchCartItems();
  }, []);

  const fetchUserAddress = async () => {
    try {
      const response = await axios.get("http://localhost:8000/medical_store_app/user/address/");
      if (response.data.address) {
        setUserAddress(response.data.address);
      }
    } catch (error) {
      console.error("Error fetching user address:", error);
    }
  };

  const fetchCartItems = async () => {
    try {
      const response = await axios.get("http://localhost:8000/medical_store_app/cart/");
      setCartItems(response.data.items || []);
    } catch (error) {
      console.error("Error fetching cart items:", error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (address.trim() === "") {
      alert("Please enter a valid address.");
      return;
    }

    setLoading(true);

    try {
      const orderData = {
        payment_method: paymentMethod,
        address: address,
        items: cartItems, // Include cart items in the order data
      };

      const response = await axios.post("http://localhost:8000/medical_store_app/order/", orderData);
      console.log("Order submitted successfully:", response.data);
      setIsSubmitted(true);

      setTimeout(() => {
        navigate("/order-confirmation");
      }, 2000);

    } catch (error) {
      console.error("Error submitting order:", error);
      alert("There was an error submitting your order. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {loading && (
        <span style={{ display: "flex" }}>
          <h5>Getting Checkout Info...</h5>
        </span>
      )}

      {!loading && (
        <div style={styles.container}>
          <h3>Checkout Summary</h3>

          <div style={styles.formGroup}>
            <h3>Billing Address</h3>
            {userAddress ? (
              <div>
                <p>{userAddress}</p>
                <Link className="ml-2 mt-2" to="/all-addresses/">Edit/Add Address</Link>
              </div>
            ) : (
              <div>
                <p>No address found. Please <Link to="/all-addresses/">add a billing address</Link>.</p>
              </div>
            )}
          </div>

          <div style={styles.formGroup}>
            <h3>Cart Items</h3>
            {cartItems.length > 0 ? (
              <ul>
                {cartItems.map((item, index) => (
                  <li key={index}>
                    <strong>{item.name}</strong> - {item.quantity} x ${item.price.toFixed(2)}
                  </li>
                ))}
              </ul>
            ) : (
              <p>No items in the cart.</p>
            )}
          </div>

          <div style={styles.formGroup}>
            <h3>Payments Section</h3>
            <label>
              Payment Method:
              <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                <option value="COD">Cash on Delivery</option>
                <option value="Credit Card">Credit Card</option>
              </select>
            </label>
          </div>

          <button
            style={styles.submitButton}
            onClick={handleSubmit}
            disabled={loading}
          >
            {loading ? "Submitting Order..." : "Submit Order"}
          </button>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "80px",
    fontFamily: "Arial, sans-serif",
    maxWidth: "600px",
    margin: "0 auto",
    backgroundColor: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  heading: {
    textAlign: "center",
    fontSize: "2rem",
    marginBottom: "20px",
    color: "#333",
  },
  formGroup: {
    marginBottom: "15px",
  },
  label: {
    fontSize: "1.1rem",
    fontWeight: "bold",
    marginBottom: "5px",
    display: "block",
  },
  input: {
    width: "100%",
    padding: "10px",
    fontSize: "1rem",
    borderRadius: "4px",
    border: "1px solid #ccc",
    boxSizing: "border-box",
    height: "100px",
  },
  radioContainer: {
    display: "flex",
    flexDirection: "column",
  },
  radioLabel: {
    fontSize: "1rem",
    marginBottom: "10px",
  },
  submitButton: {
    width: "100%",
    padding: "12px",
    fontSize: "1.2rem",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  },
  successMessage: {
    fontSize: "1.2rem",
    color: "#4CAF50",
    textAlign: "center",
    fontWeight: "bold",
  },
};

export default Checkout;
