import React from "react";
import { useLocation } from "react-router-dom";

const Booking = () => {
  const location = useLocation();
  const { order = {}, paymentDetails = {} } = location.state || {}; // Safeguard against undefined state

  const formatAmount = (amount) => (amount ? (amount / 100).toFixed(2) : "N/A");

  return (
    <div
      style={{
        padding: "20px",
        fontFamily: "Arial, sans-serif",
        backgroundColor: "#f9f9f9",
        borderRadius: "8px",
        width: "80%",
        margin: "20px auto",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      }}
    >
      <h2
        style={{
          fontSize: "24px",
          color: "#333",
          textAlign: "center",
          marginBottom: "20px",
        }}
      >
        Booking Details
      </h2>
      <p
        style={{
          fontSize: "18px",
          color: "#555",
          margin: "10px 0",
        }}
      >
        Order ID: {order.id || "N/A"}
      </p>
      <p
        style={{
          fontSize: "18px",
          color: "#555",
          margin: "10px 0",
        }}
      >
        Total Price: ₹{formatAmount(order.amount)}
      </p>
      <h3
        style={{
          fontSize: "20px",
          color: "#444",
          marginTop: "20px",
        }}
      >
        Payment Details:
      </h3>
      <p
        style={{
          fontSize: "18px",
          color: "#555",
          margin: "10px 0",
        }}
      >
        Payment ID: {paymentDetails.razorpay_payment_id || "N/A"}
      </p>
      <p
        style={{
          fontSize: "18px",
          color: "#555",
          margin: "10px 0",
        }}
      >
        Order ID: {paymentDetails.razorpay_order_id || "N/A"}
      </p>
      <p
        style={{
          fontSize: "18px",
          color: "#555",
          margin: "10px 0",
        }}
      >
        Amount Paid: ₹{formatAmount(paymentDetails.amount)}
      </p>
      <p
        style={{
          fontSize: "18px",
          color: "#555",
          margin: "10px 0",
        }}
      >
        Signature: {paymentDetails.razorpay_signature || "N/A"}
      </p>
    </div>
  );
};

export default Booking;
