import React, { useState, useEffect } from "react";
import axios from "axios";

const Booking = () => {
  const [paymentDetails, setPaymentDetails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPaymentData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/medical_store_app/payment_details/"
        );
        console.log("API Response: ", response.data);

        if (response.data.paymentDetails) {
          setPaymentDetails(response.data.paymentDetails);
        } else {
          setPaymentDetails([]);
        }
      } catch (error) {
        console.error("Error fetching payment details:", error);
        setError("Failed to load booking details.");
      } finally {
        setLoading(false);
      }
    };

    fetchPaymentData();
  }, []);

  const updateStatus = async (id, newStatus) => {
    try {
      await axios.patch(
        `http://localhost:8000/medical_store_app/payment_details/${id}/`,
        { status: newStatus }
      );
      setPaymentDetails((prevDetails) =>
        prevDetails.map((payment) =>
          payment.id === id ? { ...payment, status: newStatus } : payment
        )
      );
    } catch (error) {
      console.error("Error updating status:", error);
      alert("Failed to update status. Please try again.");
    }
  };

  return (
    <div style={styles.containerStyle}>
      <h1 style={styles.titleStyle}>Booking Details</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p style={styles.errorStyle}>{error}</p>
      ) : (
        <table style={styles.tableStyle}>
          <thead>
            <tr>
              <th style={styles.headerStyle}>Field</th>
              <th style={styles.headerStyle}>Value</th>
              <th style={styles.headerStyle}>Status</th>
              <th style={styles.headerStyle}>Update Status</th>
            </tr>
          </thead>
          <tbody>
            {paymentDetails.map((payment, index) => (
              <tr key={index}>
                <td style={styles.cellStyle}>User</td>
                <td style={styles.cellStyle}>{payment.user || "N/A"}</td>
                <td style={styles.cellStyle}>{payment.status || "Pending"}</td>
                <td style={styles.cellStyle}>
                  <select
                    value={payment.status || "Pending"}
                    onChange={(e) => updateStatus(payment.id, e.target.value)}
                  >
                    <option value="Pending">Pending</option>
                    <option value="Approved">Approved</option>
                    <option value="Declined">Declined</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  containerStyle: {
    margin: "50px auto",
    padding: "20px",
    maxWidth: "800px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    backgroundColor: "#f9f9f9",
  },
  titleStyle: {
    textAlign: "center",
    marginBottom: "20px",
  },
  tableStyle: {
    width: "100%",
    borderCollapse: "collapse",
  },
  headerStyle: {
    borderBottom: "2px solid #000",
    padding: "10px",
    textAlign: "left",
    fontWeight: "bold",
  },
  cellStyle: {
    padding: "10px",
    borderBottom: "1px solid #ddd",
  },
  errorStyle: {
    textAlign: "center",
    color: "red",
    fontSize: "18px",
  },
};

export default Booking;
