import React, { useEffect, useState } from "react";

const UserProductViews = () => {
  const [medicions, setMedicions] = useState([]);
  const [cart, setCart] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    // Fetching the medicines from the backend
    const fetchMedicions = async () => {
      try {
        const response = await fetch("/medicions/");
        const data = await response.json();
        setMedicions(data);
      } catch (error) {
        console.error("Error fetching medicines:", error);
      }
    };

    fetchMedicions();
  }, []);

  const filteredMedicions = medicions.filter((medicion) =>
    medicion.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const addToCart = (medicion) => {
    setCart((prevCart) => [...prevCart, { ...medicion, quantity: 1 }]);
  };

  const removeFromCart = (medicionId) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== medicionId));
  };

  return (
    <div>
      <h1>Available Medicines</h1>
      <input
        type="text"
        placeholder="Search for medicines..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <div>
        {filteredMedicions.length > 0 ? (
          filteredMedicions.map((medicion) => (
            <div key={medicion.id}>
              <h3>{medicion.name}</h3>
              <button onClick={() => addToCart(medicion)}>Add to Cart</button>
            </div>
          ))
        ) : (
          <p>No medicines found</p>
        )}
      </div>

      <h2>Your Cart</h2>
      <div>
        {cart.map((item) => (
          <div key={item.id}>
            <span>{item.name}</span>
            <button onClick={() => removeFromCart(item.id)}>Remove</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserProductViews;
