import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Bar } from 'react-chartjs-2';

const Dashboard = () => {
  const [salesData, setSalesData] = useState([]);
  const [inventoryData, setInventoryData] = useState([]);
  const [topProducts, setTopProducts] = useState([]);
  const [alerts, setAlerts] = useState([]);

  // Fetch data from API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const salesResponse = await axios.get('/api/sales');
        const inventoryResponse = await axios.get('/api/inventory');
        const productsResponse = await axios.get('/api/top-products');
        const alertsResponse = await axios.get('/api/alerts');

        setSalesData(salesResponse.data);
        setInventoryData(inventoryResponse.data);
        setTopProducts(productsResponse.data);
        setAlerts(alertsResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Sales Chart Data
  const salesChartData = {
    labels: salesData.map(entry => entry.date), // Dates for X-axis
    datasets: [
      {
        label: 'Sales',
        data: salesData.map(entry => entry.total), // Sales values for Y-axis
        borderColor: 'rgba(75,192,192,1)',
        backgroundColor: 'rgba(75,192,192,0.2)',
        fill: true,
      },
    ],
  };

  // Inventory Chart Data
  const inventoryChartData = {
    labels: inventoryData.map(item => item.productName),
    datasets: [
      {
        label: 'Stock Levels',
        data: inventoryData.map(item => item.stock),
        backgroundColor: 'rgba(255,99,132,0.2)',
        borderColor: 'rgba(255,99,132,1)',
        borderWidth: 1,
      },
    ],
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Medical Store Dashboard</h1>
      
      <div>
        <h3>Sales Overview</h3>
        <Line data={salesChartData} />
      </div>
      
      <div>
        <h3>Top Products</h3>
        <ul>
          {topProducts.map(product => (
            <li key={product.id}>{product.name}: {product.sales} sold</li>
          ))}
        </ul>
      </div>
      
      <div>
        <h3>Inventory Levels</h3>
        <Bar data={inventoryChartData} />
      </div>

      <div>
        <h3>Alerts</h3>
        <ul>
          {alerts.map(alert => (
            <li key={alert.id}>{alert.message}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
