import React, { useEffect, useState } from "react";
import axios from "axios";

const MedicineBookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get("http://localhost:8000/medical_store_app/admin/bookings/");
        setBookings(response.data);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchBookings();
  }, []);

  const updateStatus = async (bookingId, status) => {
    try {
      await axios.post(`http://localhost:8000/medical_store_app/admin/bookings/${bookingId}/update/`, {
        status: status,
      });
      alert("Booking status updated!");
      setBookings(
        bookings.map((booking) =>
          booking.id === bookingId ? { ...booking, status } : booking
        )
      );
    } catch (error) {
      console.error("Error updating booking status:", error);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Admin - Manage Bookings</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.header}>Customer</th>
            <th style={styles.header}>Medicine</th>
            <th style={styles.header}>Quantity</th>
            <th style={styles.header}>Status</th>
            <th style={styles.header}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking.id}>
              <td style={styles.cell}>{booking.customer.username}</td>
              <td style={styles.cell}>{booking.product.name}</td>
              <td style={styles.cell}>{booking.quantity}</td>
              <td style={styles.cell}>{booking.status}</td>
              <td style={styles.cell}>
                <button
                  onClick={() => updateStatus(booking.id, "confirmed")}
                  style={styles.confirmButton}
                >
                  Confirm
                </button>
                <button
                  onClick={() => updateStatus(booking.id, "rejected")}
                  style={styles.rejectButton}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Updated styles
const styles = {
  container: {
    padding: "9px",
    fontFamily: "'Roboto', Arial, sans-serif",
    backgroundColor: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  heading: {
    textAlign: "center",
    marginBottom: "20px",
    fontSize: "24px",
    color: "#333",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    backgroundColor: "white",
    borderRadius: "8px",
    overflow: "hidden",
  },
  header: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "15px",
    textAlign: "left",
    fontWeight: "bold",
    fontSize: "16px",
    borderBottom: "2px solid #ddd",
  },
  cell: {
    padding: "10px",
    borderBottom: "1px solid #ddd",
    fontSize: "14px",
    color: "#333",
  },
  confirmButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "8px 12px",
    margin: "0 5px",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s",
  },
  rejectButton: {
    backgroundColor: "#f44336",
    color: "white",
    border: "none",
    padding: "8px 12px",
    margin: "0 5px",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s",
  },
};

export default MedicineBookings;
