// Inline CSS styles
const styles = {
  cartPage: {
    maxWidth: "600px",
    margin: "2em auto",
    padding: "1em",
    background: "#fff",
    borderRadius: "8px",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
  },
  cartTitle: {
    fontSize: "24px",
    marginBottom: "1em",
    textAlign: "center",
  },
  emptyCart: {
    textAlign: "center",
    fontSize: "16px",
  },
  cartList: {
    listStyle: "none",
    padding: "0",
  },
  cartItem: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "0.5em 0",
    borderBottom: "1px solid #ddd",
  },
  medicineName: {
    fontSize: "18px",
  },
  price: {
    fontSize: "16px",
  },
  quantity: {
    fontSize: "16px",
  },
  removeButton: {
    padding: "5px 10px",
    color: "#fff",
    backgroundColor: "#e74c3c",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  cartTotal: {
    marginTop: "1em",
    fontSize: "18px",
    textAlign: "right",
  },
  checkoutButton: {
    padding: "10px 20px",
    color: "#fff",
    backgroundColor: "#2ecc71",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
};
