import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [paymentType, setPaymentType] = useState("Card");
  const [cardDetails, setCardDetails] = useState({
    cardNumber: "",
    cvv: "",
    expiryDate: "",
  });
  const [address, setAddress] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCartData = async () => {
      try {
        const userId = sessionStorage.getItem("user_id");
        if (!userId) {
          alert("Please log in to view your cart.");
          navigate("/login");
          return;
        }
        const cartResponse = await axios.post("http://localhost:8000/medical_store_app/cart/", {
          user_id: userId,
        });

        if (cartResponse.data && cartResponse.data.cart_items) {
          setCartItems(cartResponse.data.cart_items);
          setTotalPrice(cartResponse.data.total_price);
        } else {
          console.error("Invalid cart data:", cartResponse.data);
        }

        const addressResponse = await axios.get(`http://localhost:8000/medical_store_app/get_address/${userId}`);
        if (addressResponse.data) {
          const { street, city, state, postal_code, phone_no } = addressResponse.data;
          const fullAddress = `${street}, ${city}, ${state} - ${postal_code}, ${phone_no}`;
          setAddress(fullAddress);
        } else {
          console.error("No address found for user:", userId);
          setAddress("");
        }
      } catch (error) {
        console.error("Error fetching data:", error);
        setAddress("");
      }
    };

    fetchCartData();
  }, [navigate]);

  const handlePaymentTypeChange = (e) => {
    setPaymentType(e.target.value);
    if (e.target.value !== "Card") {
      setCardDetails({ cardNumber: "", cvv: "", expiryDate: "" });
    }
  };

  const handleCardDetailsChange = (e) => {
    const { name, value } = e.target;
    setCardDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handlePayment = async () => {
    if (!address.trim()) {
      alert("Please add your billing address.");
      return;
    }

    const userId = sessionStorage.getItem("user_id");
    if (!userId) {
      alert("Please log in.");
      navigate("/login");
      return;
    }

    

    if (paymentType === "Card") {
      const { cardNumber, cvv, expiryDate } = cardDetails;
      if (!cardNumber || !cvv || !expiryDate) {
        alert("Please fill in all card details.");
        return;
      }
      paymentData.card_details = cardDetails;
      try {
        const response = await axios.post("http://localhost:8000/medical_store_app/card-payment/", paymentData, {
          headers: {
            "Content-Type": "application/json",
          },
        });

        if (response.data.status === "success") {
          alert(response.data.message);
          navigate("/UserProductViews");
        } else {
          alert("Payment failed: " + response.data.error);
        }
      } catch (error) {
        console.error("Payment error:", error);
        alert("An error occurred during payment.");
      }
    } else if (paymentType === "Cash") {
      try {
        const response = await axios.post("http://localhost:8000/medical_store_app/cod-order/", paymentData, {
          headers: {
            "Content-Type": "application/json",
          },
        });

        if (response.data.status === "success") {
          alert(response.data.message);
          navigate("/UserProductViews");
        } else {
          alert("Order failed: " + response.data.error);
        }
      } catch (error) {
        console.error("Order error:", error);
        alert("An error occurred with your order.");
      }
    }
  };

  return (
    <div className="checkout-container">
      <h1>Checkout</h1>
      <div>
        <h3>Cart Items:</h3>
        <ul>
          {cartItems.map((item, index) => (
            <li key={index}>
              {item.name} - ${item.price} x {item.quantity}
            </li>
          ))}
        </ul>
        <p>Total Price: ${totalPrice}</p>
      </div>

      <div>
        <h3>Delivery Address:</h3>
        <p>{address || "No address found. Please add your address."}</p>
      </div>

      <div>
        <h3>Payment Method:</h3>
        <label>
          <input
            type="radio"
            name="paymentType"
            value="Card"
            checked={paymentType === "Card"}
            onChange={handlePaymentTypeChange}
          />
          Credit/Debit Card
        </label>
        <label>
          <input
            type="radio"
            name="paymentType"
            value="Cash"
            checked={paymentType === "Cash"}
            onChange={handlePaymentTypeChange}
          />
          Cash on Delivery
        </label>
      </div>

      {paymentType === "Card" && (
        <div>
          <h3>Card Details:</h3>
          <input
            type="text"
            name="cardNumber"
            placeholder="Card Number"
            value={cardDetails.cardNumber}
            onChange={handleCardDetailsChange}
          />
          <input
            type="text"
            name="cvv"
            placeholder="CVV"
            value={cardDetails.cvv}
            onChange={handleCardDetailsChange}
          />
          <input
            type="text"
            name="expiryDate"
            placeholder="Expiry Date"
            value={cardDetails.expiryDate}
            onChange={handleCardDetailsChange}
          />
        </div>
      )}

      <button onClick={handlePayment}>Complete Payment</button>
    </div>
  );
};

export default Checkout;
