import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import Login from "./pages/Login.js";
import Register from "./pages/Register";
import Home from "./pages/Home.js";
import Navbar from ".//Navbar.js"; 
import ListMedicines from "./pages/ListMedicine.js";
import AddEditMedicine from "./pages/AddEditMedicine.js";
import MedicineBookings from "./pages/MedicineBookings.js";
import AllUsers from "./pages/AllUsers";
import Cart from "./pages/cart";
import UserProductViews from "./Userpage/userproductviews";
import ChangePassword from "./Userpage/Changepassword";

// Layout Component to handle Navbar rendering
function Layout({ children }) {
  const location = useLocation();
  const token = sessionStorage.getItem("access_token");

  // Routes where the Navbar should not be shown
  const noNavbarRoutes = ["/", "/login", "/register"];

  // Check if user is logged in and show the appropriate navbar
  const showNavbar = !noNavbarRoutes.includes(location.pathname);

  return (
    <>
      {showNavbar && <Navbar />}
      {children}
    </>
  );
}

function MedicalStoreApp() {
  return (
    <div className="App">
      <Router>
        <Layout>
          <Routes>
            {/* Pre-login Routes */}
            <Route path="/" element={<Home />} />
            <Route path="login" element={<Login />} />
            <Route path="register" element={<Register />} />

            {/* Post-login Routes */}
            <Route path="/ListMedicine" element={<ListMedicines />} />
            <Route path="/MedicineBookings" element={<MedicineBookings />} />
            <Route path="/ad/AddEditMedicine" element={<AddEditMedicine />} />
            <Route path="/admin/User" element={<AllUsers />} />
            <Route path="/Cart" element={<Cart />} />
            <Route path="/UserProductViews" element={<UserProductViews />} />
            <Route path="/ChangePassword" element={<ChangePassword />} />
          </Routes>
        </Layout>
      </Router>
    </div>
  );
}

export default MedicalStoreApp;
