import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const CheckoutPage = ({ match, userInfo, product, error, loading }) => {
  const navigate = useNavigate();
  const [addressSelected, setAddressSelected] = useState(false);
  const [selectedAddressId, setSelectedAddressId] = useState(0);

  // Set address ID handler
  const handleAddressId = (id) => {
    if (id) {
      setAddressSelected(true);
    }
    setSelectedAddressId(id);
  };

  useEffect(() => {
    if (!userInfo) {
      navigate('/login');
    }
  }, [navigate, userInfo]);

  if (!userInfo) {
    alert('Session expired, please login again.');
    navigate('/login');
    window.location.reload();
  }

  return (
    <div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {loading && <p>Loading Checkout Info...</p>}
      {!loading && (
        <div style={{ padding: '20px' }}>
          <div style={{ marginBottom: '20px' }}>
            <h3>Checkout Summary</h3>
            <div
              style={{
                border: '1px solid #ddd',
                borderRadius: '5px',
                padding: '15px',
                display: 'flex',
              }}
            >
              <img
                src={product?.image}
                alt="Product"
                style={{ width: '150px', height: '150px', marginRight: '20px' }}
              />
              <div>
                <h5>{product?.name}</h5>
                <p style={{ color: 'green' }}>â‚¹ {product?.price}</p>
              </div>
            </div>
          </div>
          <div style={{ marginBottom: '20px' }}>
            <h3>Billing Address</h3>
            <Link to="/all-addresses/">Edit/Add Address</Link>
            <div>
              <p>No address selected</p>
            </div>
          </div>
          <div>
            <h3>Payments Section</h3>
            {addressSelected ? (
              <div>
                <p>Proceeding to payment...</p>
              </div>
            ) : (
              <div>
                <p>Please select an address first.</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default CheckoutPage;
