import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const Booking = () => {
  const location = useLocation();
  const { order } = location.state || {};
  const [orderDetails, setOrderDetails] = useState(order || null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!orderDetails) {
      // Fetch order details if not passed via navigation state
      const fetchOrderDetails = async () => {
        try {
          const response = await axios.get(
            `http://localhost:8000/medical_store_app/order_details/${order.id}/`
          );
          setOrderDetails(response.data.order);
        } catch (err) {
          setError("Failed to fetch order details.");
          console.error(err);
        }
      };

      fetchOrderDetails();
    }
  }, [orderDetails, order]);

  return (
    <div style={styles.container}>
      <h1>Booking Confirmation</h1>
      {error ? (
        <p style={styles.error}>{error}</p>
      ) : orderDetails ? (
        <div style={styles.orderDetails}>
          <h2>Order Details:</h2>
          <p>Order ID: {orderDetails.id}</p>
          <p>Total Price: ${orderDetails.total_price}</p>
          <p>Order Date: {orderDetails.date}</p>
          {/* Add more details based on the backend response */}
        </div>
      ) : (
        <p>Loading order details...</p>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  orderDetails: {
    backgroundColor: "#f9f9f9",
    padding: "15px",
    borderRadius: "5px",
  },
  error: {
    color: "red",
    fontWeight: "bold",
  },
};

export default Booking;
