import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiUrl } from "../axios"; // Ensure this is the correct API URL

const Register = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    username: '',
    mobile_no: '',
    email: '',
    password: '',
    cpassword: ''
  });
  const [formError, setFormError] = useState({});
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate password matching
    if (formData.password !== formData.cpassword) {
      setFormError({ password: "Passwords do not match" });
      return;
    }

    console.log("Form Data:", formData); // Log form data before sending
    console.log("Request URL:", `${apiUrl}/register/`); // Log the request URL

    try {
      const response = await axios.post(`${apiUrl}//register/`, formData);
      console.log("Response Data:", response.data); // Log success response
      setMessage(response.data.message);
    } catch (error) {
      console.error('Error:', error); // Log error for debugging
      console.error('Request URL:', `${apiUrl}/register/`); // Log the request URL again
      setMessage(error.response?.data?.error || "Something went wrong.");
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)",
      }}
    >
      <div
        className="container mt-5"
        style={{
          width: "430px",
          height: "auto",
          boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)",
          backgroundColor: "#ffffff",
          padding: "40px",
          borderRadius: "15px",
          border: "2px solid #007bff",
        }}
      >
        <form onSubmit={handleSubmit} method="post" className="form">
          <div className="row mt-3 mb-3">
            <h2 className="text-center" style={{ fontSize: "30px", color: "#007bff" ,textAlign:"center"}}>
              <i className="bi bi-person-plus px-3"></i>Register
            </h2>
          </div>

          {/* First Name */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="first_name"
              style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
            >
              First Name:
            </label>
            <input
              required
              type="text"
              name="first_name"
              id="first_name"
              placeholder="Enter your first name"
              className="form-control"
              style={{
                fontSize: "16px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
              }}
              onChange={handleChange}
            />
          </div>

          {/* Last Name */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="last_name"
              style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
            >
              Last Name:
            </label>
            <input
              type="text"
              name="last_name"
              id="last_name"
              placeholder="Enter your last name"
              className="form-control"
              style={{
                fontSize: "16px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
              }}
              onChange={handleChange}
            />
          </div>

          {/* Username */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="username"
              style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
            >
              Username:
            </label>
            <input
              required
              type="text"
              name="username"
              id="username"
              placeholder="Enter your username"
              className="form-control"
              style={{
                fontSize: "16px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
              }}
              onChange={handleChange}
            />
          </div>

          {/* Mobile Number */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="mobile"
              style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
            >
              Mobile Number:
            </label>
            <input
              required
              type="text"
              name="mobile_no"
              id="mobile"
              placeholder="Enter your mobile number"
              title="Please enter a valid phone number"
              className="form-control"
              style={{
                fontSize: "16px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
              }}
              onChange={handleChange}
            />
            <p className="text-danger" style={{ fontSize: "14px" }}>{formError.mobile_no}</p>
          </div>

          {/* Email */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="email"
              style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
            >
              Email:
            </label>
            <input
              required
              type="email"
              name="email"
              id="email"
              placeholder="Enter your email"
              className="form-control"
              style={{
                fontSize: "16px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
              }}
              onChange={handleChange}
            />
          </div>

          {/* Password */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="password"
              style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
            >
              Password:
            </label>
            <input
              required
              type="password"
              name="password"
              id="password"
              placeholder="Enter your password"
              title="Must contain at least one number, one uppercase, and lowercase letter, and be at least 8 characters long"
              className="form-control"
              style={{
                fontSize: "16px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
              }}
              onChange={handleChange}
            />
          </div>

          {/* Confirm Password */}
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="cpassword"
              style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}
            >
              Confirm Password:
            </label>
            <input
              required
              type="password"
              name="cpassword"
              id="cpassword"
              placeholder="Confirm your password"
              className="form-control"
              style={{
                fontSize: "16px",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
              }}
              onChange={handleChange}
            />
            <p className="text-danger" style={{ fontSize: "14px" }}>{formError.password}</p>
          </div>

          {/* Submit Button */}
          <div className="text-center">
            <button
              type="submit"
              className="btn btn-dark"
              style={{
                width: "50%",
                fontSize: "17px",
                padding: "10px 20px",
                backgroundColor: "#007bff",
                border: "none",
                borderRadius: "7px",
              }}
            >
              Register
            </button>
          </div>
        </form>
        {message && <p className="text-center mt-3">{message}</p>} {/* Display messages */}
      </div>
    </div>
  );
};

export default Register;
