import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const [showProductDropdown, setShowProductDropdown] = useState(false);
  const navigate = useNavigate();

  const toggleProductDropdown = () => {
    setShowProductDropdown(!showProductDropdown);
  };

  const handleLogout = () => {
    sessionStorage.clear();
    navigate('/login');
  };

  const styles = {
    navbar: {
      background: 'linear-gradient(90deg, #1e3c72, #2a5298)',
      padding: '15px 30px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      position: 'fixed',
      top: 0,
      width: '100%',
      zIndex: 1000,
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    navLinks: {
      display: 'flex',
      alignItems: 'center',
      gap: '25px',
    },
    navLink: {
      color: 'white',
      textDecoration: 'none',
      padding: '10px 20px',
      borderRadius: '25px',
      fontWeight: 'bold',
      transition: 'all 0.3s ease',
      background: 'rgba(255, 255, 255, 0.1)',
      cursor: 'pointer',
    },
    navLinkHover: {
      backgroundColor: 'rgba(255, 255, 255, 0.3)',
    },
    dropdownMenu: {
      position: 'absolute',
      top: '50px',
      left: 0,
      backgroundColor: 'rgba(255, 255, 255, 0.95)',
      borderRadius: '8px',
      padding: '10px 15px',
      zIndex: 1,
      boxShadow: '0px 4px 12px rgba(0,0,0,0.15)',
    },
    dropdownItem: {
      color: '#333',
      padding: '10px 15px',
      textDecoration: 'none',
      display: 'block',
      backgroundColor: '#f5f5f5',
      margin: '5px 0',
      borderRadius: '4px',
      transition: 'background-color 0.3s ease',
      cursor: 'pointer',
    },
    logo: {
      color: 'white',
      fontSize: '22px',
      fontWeight: 'bold',
      textDecoration: 'none',
    },
    welcomeText: {
      color: 'white',
      fontSize: '15px',
      marginRight: '14px',
    },
  };

  return (
    <header style={styles.navbar}>
      <Link to="/User" style={styles.logo}>
        Online Medical Shop Admin
      </Link>
      <nav style={styles.navLinks}>
        <div
          style={{ ...styles.navLink, ...styles.navLinkHover }}
          onMouseEnter={(e) => (e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.3)')}
          onMouseLeave={(e) => (e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.1)')}
          onClick={() => navigate('/User')}
        >
          View Users
        </div>
        <div
          style={{ ...styles.navLink, ...styles.navLinkHover }}
          onMouseEnter={(e) => (e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.3)')}
          onMouseLeave={(e) => (e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.1)')}
          onClick={() => navigate('/MedicineBookings')}
        >
          View Bookings
        </div>
        <div
          style={{ ...styles.navLink, ...styles.navLinkHover }}
          onMouseEnter={(e) => (e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.3)')}
          onMouseLeave={(e) => (e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.1)')}
          onClick={() => navigate('/ChangePassword')}
        >
          Change Password
        </div>
        <div style={{ position: 'relative' }}>
          <div
            style={{ ...styles.navLink, ...styles.navLinkHover }}
            onClick={toggleProductDropdown}
          >
            Medicines â–¼
          </div>
          {showProductDropdown && (
            <div style={styles.dropdownMenu}>
              <div
                style={styles.dropdownItem}
                onClick={() => navigate('/ad/AddEditMedicine')}
              >
                Add/Edit Medicine
              </div>
              <div
                style={styles.dropdownItem}
                onClick={() => navigate('/ListMedicine')}
              >
                List Medicines
              </div>
            </div>
          )}
        </div>
        <span style={styles.welcomeText} onClick={handleLogout}>
          <i className="bi bi-person-circle"></i> Welcome,{' '}
          {sessionStorage.getItem('name')}
        </span>
      </nav>
    </header>
  );
};

export default Navbar;
