import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiUrl } from "../axios"; 
import { jwtDecode } from "jwt-decode"; 

export default function Login() {
  const navigate = useNavigate();
  const [formValues, setFormValues] = useState({});
  const [formError, setFormError] = useState("");

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    axios
      .post(apiUrl + "/api/token/", formValues)
      .then((response) => {
        sessionStorage.setItem("access_token", response.data.access);
        sessionStorage.setItem("refresh_token", response.data.refresh);
        
        const user = jwtDecode(response.data.access); // Decode the token
        const userId = user.user_id; // Get user ID
        const username = user.name; // Get username

        // Store user information in session storage
        sessionStorage.setItem("user_id", userId);
        sessionStorage.setItem("name", username);

        // Navigate to a default page after login
        navigate("/home"); // Adjust this to the default page for all users
      })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          setFormError(error.response.data.detail);
        }
      });
  };
  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "lightgray",
      }}
    >
      <div
        className="container mt-5"
        style={{
          maxWidth: "986px", // Keeping original width for full-screen responsiveness
          boxShadow: "2px 3px 8px black",
          backgroundColor: "lightblue",
          padding: "20px",
          borderRadius: "10px",
          marginTop: "20px", // Adds margin to separate from the top
        }}
      >
        <form onSubmit={onSubmit} method="post" className="form">
          <div className="row mt-3 mb-3">
            <h4 className="text-center">
              <i className="bi bi-person-circle px-3"></i>Login
            </h4>
          </div>
          <div className="row mb-3">
            <div className="col">
              <label htmlFor="" className="form-label">
                Username
              </label>
              <input
                type="text"
                name="username"
                placeholder="username"
                className="form-control"
                onChange={onChangeHandler}
              />
            </div>
          </div>
          <div className="row mb-3">
            <div className="col">
              <label htmlFor="" className="form-label">
                Password
              </label>
              <input
                type="password"
                name="password"
                placeholder="password"
                className="form-control"
                onChange={onChangeHandler}
              />
            </div>
          </div>
          <p className="text-danger">{formError}</p>
          <p>
            New User? <a href="register">Register here</a>
            <br />
          </p>
          <div className="row mb-3">
            <div className="col">
              <button className="btn btn-dark col-3">Login</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );  
}
