import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [paymentType, setPaymentType] = useState("Card");
  const [cardDetails, setCardDetails] = useState({
    cardNumber: "",
    cvv: "",
    expiryDate: "",
  });
  const [address, setAddress] = useState("");
  const [addressChanged, setAddressChanged] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCartData = async () => {
      try {
        const userId = sessionStorage.getItem("user_id");
        if (!userId) {
          alert("Please log in to view your cart.");
          navigate("/login");
          return;
        }
        const cartResponse = await axios.post("http://localhost:8000/medical_store_app/cart/", {
          user_id: userId,
        });

        if (cartResponse.data && cartResponse.data.cart_items) {
          setCartItems(cartResponse.data.cart_items);
          setTotalPrice(cartResponse.data.total_price);
        } else {
          console.error("Invalid cart data:", cartResponse.data);
        }

        const addressResponse = await axios.get(`http://localhost:8000/medical_store_app/get_address/${userId}`);
        if (addressResponse.data) {
          const { street, city, state, postal_code, phone_no } = addressResponse.data;
          const fullAddress = `${street}, ${city}, ${state} - ${postal_code}, ${phone_no}`;
          setAddress(fullAddress);
        } else {
          console.error("No address found for user:", userId);
          setAddress("");
        }
      } catch (error) {
        console.error("Error fetching data:", error);
        setAddress("");
      }
    };

    fetchCartData();
  }, [navigate]);

  const handlePaymentTypeChange = (e) => {
    setPaymentType(e.target.value);
    if (e.target.value !== "Card") {
      setCardDetails({ cardNumber: "", cvv: "", expiryDate: "" });
    }
  };

  const handleCardDetailsChange = (e) => {
    const { name, value } = e.target;
    setCardDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handlePayment = () => {
    if (!address.trim()) {
      alert("Please add your billing address.");
      return;
    }

    if (paymentType === "Card") {
      const { cardNumber, cvv, expiryDate } = cardDetails;
      if (!cardNumber || !cvv || !expiryDate) {
        alert("Please fill in all card details.");
        return;
      }
      alert("Payment successful! Thank you for your purchase.");
    } else {
      alert("Order placed successfully with Cash on Delivery!");
    }
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Checkout</h1>
      <div style={styles.mainBox}>
        <div style={styles.addressBox}>
          <h2 style={styles.subHeading}>Billing Address</h2>
          {address.trim() ? (
            <div>
              <textarea
                style={styles.textarea}
                value={address}
                disabled={!addressChanged}
                onChange={(e) => setAddress(e.target.value)}
              />
              <button
                style={styles.button}
                onClick={() => setAddressChanged(true)}
              >
                Change Address
              </button>
              {addressChanged && (
                <button
                  style={styles.button}
                  onClick={() => alert("Address saving feature is under development")}
                >
                  Save Address
                </button>
              )}
            </div>
          ) : (
            <div>
              <p>Please add your billing address.</p>
              <button onClick={() => navigate("/addaddress")}>Add Address</button>
            </div>
          )}
        </div>

        <div style={styles.paymentBox}>
          <h2 style={styles.subHeading}>Cart Details</h2>
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.tableHeader}>Medicine</th>
                <th style={styles.tableHeader}>Quantity</th>
                <th style={styles.tableHeader}>Price</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, index) => (
                <tr key={index}>
                  <td style={styles.tableData}>{item.medicine?.name || "Unknown Medicine"}</td>
                  <td style={styles.tableData}>{item.quantity || 0}</td>
                  <td style={styles.tableData}>
                    Rs.
                    {item.medicine?.price && item.quantity
                      ? item.medicine.price * item.quantity
                      : 0}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <h3 style={styles.total}>Total: Rs.{totalPrice}</h3>

          <h2 style={styles.subHeading}>Payment Details</h2>
          <div style={styles.paymentTypeContainer}>
            <label>
              <input
                type="radio"
                value="Card"
                checked={paymentType === "Card"}
                onChange={handlePaymentTypeChange}
              />
              Pay by Card
            </label>
            {paymentType === "Card" && (
              <div style={styles.cardDetailsContainer}>
                <input
                  style={styles.cardInput}
                  type="text"
                  placeholder="Card Number"
                  name="cardNumber"
                  value={cardDetails.cardNumber}
                  onChange={handleCardDetailsChange}
                />
                <input
                  style={styles.cardInput}
                  type="text"
                  placeholder="CVV"
                  name="cvv"
                  value={cardDetails.cvv}
                  onChange={handleCardDetailsChange}
                />
                <input
                  style={styles.cardInput}
                  type="text"
                  placeholder="MM/YYYY"
                  name="expiryDate"
                  value={cardDetails.expiryDate}
                  onChange={handleCardDetailsChange}
                />
              </div>
            )}
            <label>
              <input
                type="radio"
                value="Cash"
                checked={paymentType === "Cash"}
                onChange={handlePaymentTypeChange}
              />
              Cash on Delivery
            </label>
          </div>
          <button style={styles.paymentButton} onClick={handlePayment}>
            {paymentType === "Card" ? "Confirm Payment" : "Place Order"}
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  // Existing styles
  cardDetailsContainer: { display: "flex", flexDirection: "column", gap: "10px", marginTop: "10px" },
  cardInput: {
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "5px",
    width: "100%",
  },
};

export default Checkout;
