import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PaymentPage = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [medicines, setMedicines] = useState([]);
    const [totalAmount, setTotalAmount] = useState(0);

    const userId = localStorage.getItem('userId');  // Assuming userId is stored in localStorage

    // Fetch the medicines data from the API only if userId is available
    useEffect(() => {
        if (!userId) {
            setError('User ID is missing');
            return;  // Exit early if userId is not available
        }

        const fetchMedicines = async () => {
            try {
                setIsLoading(true);
                const response = await axios.get(
                    `http://localhost:8000/medical_store_app/orders/medicines/${userId}/`,  // Replace with the correct endpoint
                    {
                        headers: {
                            'Authorization': `Bearer ${localStorage.getItem('authToken')}`, // Assuming JWT token
                        },
                    }
                );
                
                // Assuming the response returns a list of medicines
                const medicinesData = response.data.medicines;
                setMedicines(medicinesData);
                calculateTotal(medicinesData);
            } catch (err) {
                console.error(err);
                setError('Failed to fetch medicines. Please try again.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchMedicines();
    }, [userId]);  // Re-run this effect if userId changes

    // Calculate the total amount from the medicines data
    const calculateTotal = (medicines) => {
        let total = 0;
        medicines.forEach((medicine) => {
            if (medicine.price && medicine.quantity) {
                total += medicine.price * medicine.quantity;
            }
        });
        setTotalAmount(total);
    };

    const handlePlaceOrder = async () => {
        try {
            setIsLoading(true);
            const response = await axios.post(
                'http://localhost:8000/medical_store_app/orders/',  // The API endpoint for placing an order
                {
                    user_id: userId,
                    medicines: medicines.map(m => m.id),  // Assuming medicines have 'id' property
                    total_amount: totalAmount,
                    payment_type: 'COD',  // Hardcoding COD for this example
                },
                {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('authToken')}`,  // Assuming JWT for authentication
                    },
                }
            );

            console.log(response.data);  // Handle success
            alert('Order placed successfully!');
        } catch (err) {
            console.error(err);
            setError('Something went wrong. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div>
            <h1>Payment Page</h1>
            <p>Payment Method: Cash on Delivery (COD)</p>
            <p>Total Amount: ${totalAmount.toFixed(2)}</p>
            
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <button onClick={handlePlaceOrder} disabled={isLoading}>
                {isLoading ? 'Placing Order...' : 'Place Order'}
            </button>
        </div>
    );
};

export default PaymentPage;
