import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import AddressForm from './Address';  // Import the AddressForm

const stripePromise = loadStripe('your_stripe_publishable_key');

const PaymentForm = () => {
  const [clientSecret, setClientSecret] = useState('');
  const [amount, setAmount] = useState(0);
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [paymentType, setPaymentType] = useState('card');
  const [selectedAddress, setSelectedAddress] = useState(null);

  const stripe = useStripe();
  const elements = useElements();

  const location = useLocation();
  const totalPrice = location.state?.totalPrice || 0;

  useEffect(() => {
    setAmount(totalPrice * 100);
  }, [totalPrice]);

  const handleAddressSubmit = () => {
    // Refresh addresses after submitting a new address
    fetchAddresses();
  };

  const fetchAddresses = async () => {
    try {
      const response = await fetch('http://localhost:8000/medical_store_app/get-addresses/');
      const data = await response.json();
      setSelectedAddress(data[0] || null);  // Use first address for example
    } catch (error) {
      setError('Failed to load addresses');
    }
  };

  useEffect(() => {
    fetchAddresses();
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Handle payment submission here, as shown in your original PaymentForm
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Payment</h2>
      <form onSubmit={handleSubmit}>
        {/* Address selection dropdown */}
        <label style={styles.label}>Select Address:</label>
        <select
          value={selectedAddress ? selectedAddress.id : ''}
          onChange={(e) => setSelectedAddress(e.target.value)}
          style={styles.input}
        >
          {selectedAddress && (
            <option value={selectedAddress.id}>
              {selectedAddress.street_address}, {selectedAddress.city}
            </option>
          )}
        </select>

        {/* Other payment details here */}

        <button type="submit" style={styles.button}>
          Submit Payment
        </button>
      </form>

      {/* Address form */}
      <AddressForm onAddressSubmit={handleAddressSubmit} />
      {error && <div style={styles.error}>{error}</div>}
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
    fontFamily: 'Arial, sans-serif',
    width: '100%',
    maxWidth: '500px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    width: '100%',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonDisabled: {
    backgroundColor: '#ccc',
    cursor: 'not-allowed',
  },
  error: {
    color: 'red',
    fontSize: '14px',
    marginTop: '10px',
  },
};

export default PaymentPage;
