import React, { useEffect, useState } from 'react';
import axios from 'axios';

const product = () => {
    const [product, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('/api/product/'); // Adjust the endpoint as necessary
                setProducts(response.data);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchProducts();
    }, []);

    if (loading) return <p style={{ color: 'blue', fontSize: '18px' }}>Loading...</p>;
    if (error) return <p style={{ color: 'red', fontSize: '18px' }}>Error: {error}</p>;

    return (
        <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
            <h1 style={{ textAlign: 'center', color: '#333' }}>Product List</h1>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
                {products.map((product) => (
                    <li key={product.id} style={{ border: '1px solid #ccc', borderRadius: '5px', margin: '10px 0', padding: '15px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' }}>
                        <h2 style={{ margin: '0', color: '#007BFF' }}>{product.name}</h2>
                        <p style={{ margin: '5px 0' }}>{product.description}</p>
                        <p style={{ fontWeight: 'bold' }}>Price: <span style={{ color: '#28A745' }}>${product.price}</span></p>
                        <p style={{ fontStyle: 'italic' }}>{product.in_stock ? 'In Stock' : 'Out of Stock'}</p>
                        <p style={{ fontSize: '12px', color: '#777' }}>Added on: {new Date(product.added_on).toLocaleString()}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default product;
