import React, { useState, useEffect } from 'react';
import './Home.css';

const HomePage = () => {
  const [medicines, setMedicines] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    // Fetch all medicines initially
    fetchMedicines();
  }, []);

  const fetchMedicines = (search = "") => {
    const url = search ? `/api/medicines/?search=${search}` : '/api/medicines/';

    fetch(url)
      .then((response) => response.json())
      .then((data) => setMedicines(data))
      .catch((error) => console.error("Error fetching medicines:", error));
  };

  const handleSearch = () => {
    fetchMedicines(searchTerm); // Fetch medicines with the search term
  };

  return (
    <div className="homepage">
      {/* Navigation Bar */}
      <nav className="navbar">
        <h1>Medical Store</h1>
        <div className="nav-options">
          <button className="nav-button">Home</button>
          <button className="nav-button">About Us</button>
          <button className="nav-button">Contact Us</button>
          <button className="nav-button">Cart</button>
          <button className="nav-button">Login/Register</button>
        </div>
      </nav>

      {/* Search Section */}
      <section className="search-section">
        <input
          type="text"
          placeholder="Search medicines..."
          className="search-input"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="search-button" onClick={handleSearch}>Search</button>
      </section>

      {/* Main Content */}
      <main className="main-content">
        <h2>Welcome to Our Medical Store</h2>
        <p>Find the best medicines at affordable prices.</p>

        <div className="medicine-list">
          {medicines.map((medicine) => (
            <div key={medicine.id} className="medicine-item">
              <h3>{medicine.name}</h3>
              <p>{medicine.description}</p>
              <p>Price: ${medicine.price}</p>
            </div>
          ))}
        </div>
      </main>

      {/* Footer */}
      <footer className="footer">
        <p>&copy; 2024 Medical Store. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
