import React, { useState, useEffect } from 'react';

// Sample data (you can replace this with data fetched from an API or backend)
const sampleProducts = [
  {
    id: 1,
    name: 'Paracetamol',
    price: '5.00',
    description: 'Used for pain relief and fever reduction.',
    inStock: true,
  },
  {
    id: 2,
    name: 'Ibuprofen',
    price: '10.00',
    description: 'Used to reduce inflammation and treat pain.',
    inStock: false,
  },
  {
    id: 3,
    name: 'Aspirin',
    price: '8.50',
    description: 'Used as a pain reliever and to reduce fever or inflammation.',
    inStock: true,
  },
];

function Product() {  // Change here: Product must start with an uppercase letter
  const [products, setProducts] = useState([]);

  // Fetch product data (if needed)
  useEffect(() => {
    // You can replace this with an API call to fetch products
    setProducts(sampleProducts);
  }, []);

  // Inline CSS Styles
  const styles = {
    container: {
      padding: '20px',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
      gap: '20px',
    },
    card: {
      border: '1px solid #ccc',
      padding: '10px',
      borderRadius: '8px',
      backgroundColor: '#f9f9f9',
      textAlign: 'center',
    },
    productName: {
      fontSize: '1.2em',
    },
    button: (inStock) => ({
      backgroundColor: inStock ? '#28a745' : '#ccc',
      color: 'white',
      padding: '10px',
      border: 'none',
      cursor: inStock ? 'pointer' : 'not-allowed',
      borderRadius: '4px',
    }),
  };

  
}

export default Product;  // Export the component
