import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { order, totalPrice, clearCart } = location.state;

  const deleteFunction = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/medical_store_app/delete_item/${id}/`);
      if (clearCart) {
        clearCart();
      }
      alert("Item deleted successfully.");
    } catch (error) {
      alert("Failed to delete the item.");
    }
  };

  useEffect(() => {
    if (!order || !totalPrice) {
      return;
    }

    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";

    script.onload = () => {
      const initializeRazorpay = () => {
        const options = {
          key: "rzp_test_a6hVvFHJbT9SAO",
          amount: totalPrice * 100,
          currency: "INR",
          name: "Your Company Name",
          description: `Payment for Order #${order.id}`,
          order_id: order.id,
          handler: async function (response) {
            try {
              const { razorpay_payment_id, razorpay_order_id, razorpay_signature } = response;

              const paymentData = {
                razorpay_payment_id,
                razorpay_order_id,
                razorpay_signature,
                amount: totalPrice * 100,
              };

              await axios.post("http://localhost:8000/medical_store_app/payment_details/", paymentData);

              if (clearCart) {
                clearCart();
              }

              navigate("/booking", {
                state: {
                  order,
                  paymentDetails: paymentData,
                },
              });
            } catch (error) {
              alert("Payment processing failed. Please try again.");
            }
          },
          prefill: {
            name: "Customer Name",
            email: "customer@example.com",
            contact: "9999999999",
          },
          theme: {
            color: "#F37254",
          },
          modal: {
            ondismiss: function () {
              alert("Payment was canceled.");
            },
          },
        };
        const razorpay = new window.Razorpay(options);
        razorpay.open();
      };
      initializeRazorpay();
    };

    script.onerror = () => {
      alert("Failed to load Razorpay script.");
    };

    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, [order, totalPrice, navigate, clearCart]);

  if (!order || !totalPrice) {
    return <div style={styles.error}>Error: Missing order or total price details.</div>;
  }

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Checkout</h2>
      <p style={styles.message}>Your payment is being processed, please wait...</p>
      <button
        style={styles.deleteButton}
        onClick={() => deleteFunction(order.id)}
      >
        Delete Order
      </button>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    textAlign: 'center',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#f4f4f9',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    maxWidth: '600px',
    margin: 'auto',
  },
  heading: {
    fontSize: '24px',
    color: '#333',
    marginBottom: '20px',
  },
  message: {
    fontSize: '18px',
    color: '#555',
  },
  error: {
    color: 'red',
    fontSize: '18px',
    textAlign: 'center',
    marginTop: '20px',
  },
  deleteButton: {
    marginTop: '20px',
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#d9534f',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default Checkout;
