import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [paymentType, setPaymentType] = useState("Card");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCartData = async () => {
      try {
        const userId = sessionStorage.getItem("user_id");
        if (!userId) {
          alert("Please log in to view your cart.");
          navigate("/login");
          return;
        }

        const response = await axios.post("http://localhost:8000/medical_store_app/cart/", {
          user_id: userId,
        });

        setCartItems(response.data.cart_items);
        setTotalPrice(response.data.total_price);
      } catch (error) {
        console.error("Error fetching cart items:", error);
      }
    };

    fetchCartData();
  }, [navigate]);

  const handlePaymentTypeChange = (e) => {
    setPaymentType(e.target.value);
  };

  const handlePayment = () => {
    if (paymentType === "Card") {
      alert("Payment successful! Thank you for your purchase.");
    } else {
      alert("Order placed successfully with Cash on Delivery!");
    }
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Checkout</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Price</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td style={styles.tableData}>{item.medicine.name}</td>
              <td style={styles.tableData}>{item.quantity}</td>
              <td style={styles.tableData}>Rs.{item.medicine.price * item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 style={styles.total}>Total: Rs.{totalPrice}</h3>
      <div style={styles.paymentContainer}>
        <h2>Payment Details</h2>
        <div style={styles.paymentTypeContainer}>
          <label>
            <input
              type="radio"
              value="Card"
              checked={paymentType === "Card"}
              onChange={handlePaymentTypeChange}
            />
            Pay by Card
          </label>
          <label>
            <input
              type="radio"
              value="Cash"
              checked={paymentType === "Cash"}
              onChange={handlePaymentTypeChange}
            />
            Cash on Delivery
          </label>
        </div>
        {paymentType === "Card" && (
          <form style={styles.form} onSubmit={handlePayment}>
            <label style={styles.label}>
              Name on Card:
              <input type="text" style={styles.input} required />
            </label>
            <label style={styles.label}>
              Card Number:
              <input
                type="text"
                style={styles.input}
                pattern="\d{16}"
                title="Enter a valid 16-digit card number"
                required
              />
            </label>
            <label style={styles.label}>
              Expiry Date:
              <input
                type="text"
                style={styles.input}
                pattern="\d{2}/\d{2}"
                title="Format: MM/YY"
                placeholder="MM/YY"
                required
              />
            </label>
            <label style={styles.label}>
              CVV:
              <input
                type="text"
                style={styles.input}
                pattern="\d{3}"
                title="Enter a valid 3-digit CVV"
                required
              />
            </label>
            <button type="submit" style={styles.paymentButton}>
              Confirm Payment
            </button>
          </form>
        )}
        {paymentType === "Cash" && (
          <div style={styles.cashContainer}>
            <p>Please have the total amount ready in cash at the time of delivery.</p>
            <button style={styles.paymentButton} onClick={handlePayment}>
              Place Order
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: { padding: "70px", fontFamily: "Arial, sans-serif" },
  heading: { textAlign: "center", fontSize: "2rem", marginBottom: "20px" },
  table: { width: "100%", borderCollapse: "collapse", marginBottom: "20px" },
  tableHeader: { backgroundColor: "#f4f4f4", padding: "10px", textAlign: "left", fontWeight: "bold" },
  tableData: { padding: "10px", borderBottom: "1px solid #ddd" },
  total: { fontSize: "1.5rem", textAlign: "right", marginTop: "20px", fontWeight: "bold" },
  paymentContainer: { marginTop: "30px", padding: "20px", border: "1px solid #ddd", borderRadius: "5px", backgroundColor: "#f9f9f9" },
  paymentTypeContainer: { display: "flex", gap: "20px", marginBottom: "20px" },
  form: { display: "flex", flexDirection: "column" },
  label: { marginBottom: "10px", fontWeight: "bold" },
  input: { padding: "10px", marginTop: "5px", marginBottom: "15px", border: "1px solid #ccc", borderRadius: "5px" },
  paymentButton: { backgroundColor: "#4CAF50", color: "white", border: "none", padding: "10px 20px", fontSize: "1rem", borderRadius: "5px", cursor: "pointer" },
  cashContainer: { textAlign: "center", marginTop: "20px" },
};

export default Checkout;
