import React from "react";
import AuthHandler from  "../utils/AuthHandler";
import APIHandler from "../utils/APIHandler";
import { Link } from "react-router-dom";

class MedicineManage extends React.Component {
  constructor(props) {
    super(props);
    this.formSubmit = this.formSubmit.bind(this);a
  }

  state = {
    errorRes: false,
    errorMessage: "",
    btnMessage: 0,
    sendData: false,
    companylist: [],
    medicinedetails: [],
    medicineDataList: [],
    dataLoaded: false,
    name: "",
    medical_typ: "",
    buy_price: "",
    sell_price: "",
    c_gst: "",
    s_gst: "",
    batch_no: "",
    shelf_no: "",
    expire_date: "",
    mfg_date: "",
    company_id: "",
    description1: "",
    in_stock_total: "",
    qty_in_strip: "",
    total_salt_list: 0,
    medicine_id: 0,
  };

  async formSubmit(event) {
    event.preventDefault();
    this.setState({ btnMessage: 1 });
    const apiHandler = new APIHandler();
    const response = await apiHandler.editMedicineData(
      event.target.name.value,
      event.target.medical_typ.value,
      event.target.buy_price.value,
      event.target.sell_price.value,
      event.target.c_gst.value,
      event.target.s_gst.value,
      event.target.batch_no.value,
      event.target.shelf_no.value,
      event.target.expire_date.value,
      event.target.mfg_date.value,
      event.target.company_id.value,
      event.target.description1.value,
      event.target.in_stock_total.value,
      event.target.qty_in_strip.value,
      this.state.medicinedetails,
      this.state.medicine_id
    );
    console.log(response);
    this.setState({ btnMessage: 0, sendData: true, errorRes: response.data.error, errorMessage: response.data.message });
  }

  componentDidMount() {
    this.LoadInitialData();
  }

  async LoadInitialData() {
    const apiHandler = new APIHandler();
    const companydata = await apiHandler.fetchCompanyOnly();
    const medicinedata = await apiHandler.fetchAllMedicine();
    this.setState({ companylist: companydata.data, medicineDataList: medicinedata.data.data, dataLoaded: true });
  }

  RemoveItems = () => {
    if (this.state.medicinedetails.length > 0) {
      const updatedMedicinedetails = this.state.medicinedetails.slice(0, -1);
      this.setState({ medicinedetails: updatedMedicinedetails });
    }
  };

  handleInput = (event) => {
    const keyname = event.target.name;
    const value = event.target.value;
    const index = event.target.getAttribute("data-index");
    
    // Create a copy of medicinedetails to avoid direct mutation
    const updatedMedicinedetails = [...this.state.medicinedetails];
    updatedMedicinedetails[index] = {
      ...updatedMedicinedetails[index],
      [keyname]: value,
    };

    this.setState({ medicinedetails: updatedMedicinedetails });
    console.log(updatedMedicinedetails);
  };

  AddItem = () => {
    const item = {
      salt_name: "",
      salt_qty: "",
      salt_qty_type: "",
      description: "",
      id: 0,
    };

    // Create a new array with the new item
    this.setState((prevState) => ({
      medicinedetails: [...prevState.medicinedetails, item],
    }));
  };

  viewmedicineDetails = (index) => {
    const medicine = this.state.medicineDataList[index];
    this.setState({
      medicine_id: medicine.id,
      name: medicine.name,
      medical_typ: medicine.medical_typ,
      buy_price: medicine.buy_price,
      sell_price: medicine.sell_price,
      c_gst: medicine.c_gst,
      s_gst: medicine.s_gst,
      batch_no: medicine.batch_no,
      shelf_no: medicine.shelf_no,
      expire_date: medicine.expire_date,
      mfg_date: medicine.mfg_date,
      company_id: medicine.company_id,
      description1: medicine.description,
      in_stock_total: medicine.in_stock_total,
      qty_in_strip: medicine.qty_in_strip,
      total_salt_list: medicine.medicine_details.length,
      medicinedetails: medicine.medicine_details,
    });
  };

  render() {
    return (
      <section className="content">
        <div className="container-fluid">
          <div className="block-header">
            <h2>Manage Medicine</h2>
          </div>

          <div className="row clearfix">
            <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
              <div className="card">
                <div className="header">
                  {!this.state.dataLoaded && (
                    <div className="text-center">
                      <div className="preloader pl-size-xl">
                        <div className="spinner-layer">
                          <div className="circle-clipper left">
                            <div className="circle"></div>
                          </div>
                          <div className="circle-clipper right">
                            <div className="circle"></div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                  <h2>All Medicine</h2>
                </div>
                <div className="body table-responsive">
                  <table className="table table-hover">
                    <thead>
                      <tr>
                        <th>#ID</th>
                        <th>NAME</th>
                        <th>Medical Type</th>
                        <th>Buy Price</th>
                        <th>Sell Price</th>
                        <th>Batch No</th>
                        <th>Shelf No</th>
                        <th>Expire Date</th>
                        <th>Mfg Date</th>
                        <th>In Stock</th>
                        <th>Company</th>
                        <th>Added On</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {this.state.medicineDataList.map((medicine) => (
                        <tr key={medicine.id}>
                          <td>{medicine.id}</td>
                          <td>{medicine.name}</td>
                          <td>{medicine.medical_typ}</td>
                          <td>{medicine.buy_price}</td>
                          <td>{medicine.sell_price}</td>
                          <td>{medicine.batch_no}</td>
                          <td>{medicine.shelf_no}</td>
                          <td>{medicine.expire_date}</td>
                          <td>{medicine.mfg_date}</td>
                          <td>{medicine.in_stock_total}</td>
                          <td>{medicine.company.name}</td>
                          <td>{new Date(medicine.added_on).toLocaleString()}</td>
                          <td>
                            <button
                              className="btn btn-block btn-warning"
                              onClick={() => this.viewmedicineDetails(medicine.id)}
                            >
                              View
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>

          <div className="row clearfix">
            <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
              <div className="card">
                <div className="header">
                  <h2>Manage Medicine</h2>
                </div>
                <div className="body">
                  <form onSubmit={this.formSubmit}>
                    <label htmlFor="email_address">Name</label>
                    <div className="form-group">
                      <div className="form-line">
                        <input
                          type="text"
                          id="name"
                          name="name"
                          className="form-control"
                          placeholder="Enter Name"
                          value={this.state.name}
                          onChange={(e) => this.setState({ name: e.target.value })}
                        />
                      </div>
                    </div>

                    {/* Similar input fields for other properties go here */}

                    <label htmlFor="email_address">Company</label>
                    <div className="form-group">
                      <select
                        className="form-control show-tick"
                        name="company_id"
                        id="company_id"
                        value={this.state.company_id}
                        onChange={(e) => this.setState({ company_id: e.target.value })}
                      >
                        {this.state.companylist.map((item) => (
                          <option key={item.id} value={item.id}>
                            {item.name}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="form-group">
                      <div className="col-lg-6">
                        <button type="button" className="btn btn-default btn-lg" onClick={this.AddItem}>
                          Add Item
                        </button>
                      </div>
                      <div className="col-lg-6">
                        <button type="submit" className="btn btn-primary btn-lg" disabled={this.state.btnMessage === 1}>
                          {this.state.btnMessage === 1 ? "Saving..." : "Save"}
                        </button>
                      </div>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default MedicineManage;
