// ForgotPassword.js
import React, { useState } from "react";
import { axiosInstance } from "../axios"; // Import the axios instance

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setEmail(e.target.value);
    setMessage(null);
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Assuming there's an endpoint for requesting a password reset
      const response = await axiosInstance.post("/api/password-reset/", {
        email,
      });
      setMessage("Password reset email sent successfully!");
    } catch (err) {
      setError("Failed to send password reset email.");
    }
  };

  return (
    <div style={{ padding: "40px", backgroundColor: "#ffffff", borderRadius: "15px", boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)" }}>
      <h2 className="text-center" style={{ fontSize: "30px", color: "#007bff" }}>Forgot Password</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: "20px" }}>
          <label htmlFor="email" style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}>Email:</label>
          <input
            required
            type="email"
            name="email"
            id="email"
            placeholder="Enter your email"
            className="form-control"
            onChange={handleChange}
          />
        </div>
        {error && <p className="text-danger">{error}</p>}
        {message && <p className="text-success">{message}</p>}
        <div className="text-center">
          <button className="btn btn-dark" style={{ width: "50%", backgroundColor: "#007bff", border: "none", borderRadius: "8px" }}>Send Reset Link</button>
        </div>
      </form>
    </div>
  );
};

export default ForgotPassword;
