import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // For navigation

const AdminDashboard = () => {
  const [showProductDropdown, setShowProductDropdown] = useState(false);

  const toggleProductDropdown = () => {
    setShowProductDropdown(!showProductDropdown);
  };

  const styles = {
    dashboard: {
      fontFamily: "'Poppins', sans-serif",
      textAlign: 'center',
      backgroundColor: '#f0f4f7',
      height: '100vh',
      margin: 0,
      color: '#333',
    },
    header: {
      backgroundColor: '#2a2a2a',
      padding: '10px 20px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
    },
    nav: {
      display: 'flex',
      alignItems: 'center',
      gap: '20px',
    },
    navLink: {
      color: 'white',
      textDecoration: 'none',
      padding: '10px 15px',
      borderRadius: '5px',
      transition: 'background-color 0.3s ease',
      fontWeight: 'bold',
    },
    navLinkHover: {
      backgroundColor: '#444',
    },
    dropdownMenu: {
      position: 'absolute',
      top: '40px',
      left: 0,
      backgroundColor: '#333',
      borderRadius: '8px',
      padding: '10px',
      zIndex: 1,
      display: 'none',
      boxShadow: '0px 4px 12px rgba(0,0,0,0.1)',
    },
    dropdownMenuOpen: {
      display: 'block',
    },
    dropdownItem: {
      color: 'white',
      padding: '10px 15px',
      textDecoration: 'none',
      display: 'block',
      backgroundColor: '#444',
      margin: '5px 0',
      borderRadius: '4px',
      transition: 'background-color 0.3s ease',
    },
    dropdownItemHover: {
      backgroundColor: '#555',
    },
    logo: {
      color: 'white',
      fontSize: '20px',
      fontWeight: 'bold',
      textDecoration: 'none',
    },
  };

  return (
    <div style={styles.dashboard}>
      <header style={styles.header}>
        <Link to="/" style={styles.logo}>
          Online Medical Shop Admin
        </Link>
        <nav style={styles.nav}>
          <Link to="/User" style={styles.navLink}>
            View User
          </Link>
          <Link to="/MedicineBookings" style={styles.navLink}>
            View Booking
          </Link>
          <Link to="/FeedbackList" style={styles.navLink}>
            Change Password
          </Link>
          <div
            style={{
              ...styles.navLink,
              position: 'relative',
              cursor: 'pointer',
            }}
            onClick={toggleProductDropdown}
          >
            Medicines â–¼
            {showProductDropdown && (
              <div style={styles.dropdownMenu}>
                <Link to="/ad/AddEditMedicine" style={styles.dropdownItem}>
                  Add/Edit Medicine
                </Link>
                <Link to="/ListMedicine" style={styles.dropdownItem}>
                  List Medicines
                </Link>
              </div>
            )}
          </div>
          <Link to="/" style={styles.navLink}>
            Logout
          </Link>
        </nav>
      </header>
    </div>
  );
};

export default AdminDashboard;
