import React, { useState } from "react";
import "./Home.css";
import Tablet from "../assets/image/Tablet.jpeg";
import syrup from "../assets/image/syrup.webp";
import Capsule from "../assets/image/Capsule.jpeg";

const Home = () => {
  const products = [
    { id: 1, name: "Tablet", image: Tablet },
    { id: 2, name: "Capsule", image: Capsule },
    { id: 3, name: "Syrup", image: syrup },
  ];

  const [cart, setCart] = useState([]); // Cart state
  const [searchTerm, setSearchTerm] = useState(""); // Search term state

  // Function to handle product search
  const onSearchChange = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
  };

  // Filtered products based on search
  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm)
  );

  // Function to add product to the cart
  const addToCart = (product) => {
    setCart([...cart, product]);
    alert(`${product.name} added to cart!`);
  };

  return (
    <div className="homepage">
      <header className="header">
        <h1>ONLINE MEDICINE STORE</h1>
        <nav className="navbar">
          <ul>
            <li><a href="/">Home</a></li>
            <li><a href="/contact">Contact</a></li>
            <li><a href="/about">About</a></li>
            <li><a href="/login">Login/Register</a></li>
          </ul>
        </nav>
      </header>

      {/* Search bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for products..."
          value={searchTerm}
          onChange={onSearchChange}
        />
      </div>

      <section className="intro">
        <h2>Online Medicine Store</h2>
        <p>
          Online medicine shopping in 2024 continues to thrive as a major form of e-commerce, allowing consumers to buy medicines and healthcare products directly from sellers through websites or mobile apps.
        </p>
      </section>

      <section className="categories">
        <h3>Shop By Category</h3>
        <div className="category-list">
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <div className="category-item" key={product.id}>
                <img src={product.image} alt={product.name} className="img-fluid" />
                <p>{product.name}</p>
                <button onClick={() => addToCart(product)} className="btn btn-primary">
                  Add to Cart
                </button>
              </div>
            ))
          ) : (
            <p>No products found</p>
          )}
        </div>
      </section>

      {/* Display Cart */}
      <section className="cart">
        <h3>Your Cart</h3>
        {cart.length > 0 ? (
          <ul>
            {cart.map((item, index) => (
              <li key={index}>{item.name}</li>
            ))}
          </ul>
        ) : (
          <p>Your cart is empty</p>
        )}
      </section>
    </div>
  );
};

export default Home;
