import React, { useEffect, useState } from "react";
import axios from "axios";

const MedicineBookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get("http://localhost:8000/medical_store_app/admin/bookings/");
        console.log(response.data);
        setBookings(response.data);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchBookings();
  }, []);

  const updateStatus = async (bookingId, status) => {
    try {
      await axios.post(`http://localhost:8000/medical_store_app/admin/bookings/${bookingId}/update/`, {
        status: status,
      });
      alert("Booking status updated!");
      setBookings(
        bookings.map((booking) =>
          booking.id === bookingId ? { ...booking, status } : booking
        )
      );
    } catch (error) {
      console.error("Error updating booking status:", error);
    }
  };

  return (
    <div style={containerStyle}>
      <h1 style={headerStyle}>Admin - Manage Bookings</h1>
      {bookings.length === 0 ? (
        <p style={{ textAlign: "center", fontSize: "18px" }}>No bookings found</p>
      ) : (
        <div style={cardContainerStyle}>
          {bookings.map((booking) => (
            <div key={booking.id} style={cardStyle}>
              <h3 style={cardTitleStyle}>{booking.customer}</h3>
              <p style={cardTextStyle}><strong>Medicine:</strong> {booking.product}</p>
              <p style={cardTextStyle}><strong>Quantity:</strong> {booking.quantity}</p>
              <p style={cardTextStyle}>
                <strong>Status:</strong> {booking.status ? booking.status : "No status"}
              </p>
              <div style={buttonGroupStyle}>
                <button
                  onClick={() => updateStatus(booking.id, "confirmed")}
                  style={buttonStyle("green")}
                >
                  Confirm
                </button>
                <button
                  onClick={() => updateStatus(booking.id, "rejected")}
                  style={buttonStyle("red")}
                >
                  Reject
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const containerStyle = {
  padding: "20px",
  fontFamily: "Arial, sans-serif",
};

const headerStyle = {
  textAlign: "center",
  marginBottom: "20px",
};

const cardContainerStyle = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
  gap: "20px",
};

const cardStyle = {
  backgroundColor: "#fff",
  border: "1px solid #ddd",
  borderRadius: "10px",
  padding: "20px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
};

const cardTitleStyle = {
  marginBottom: "10px",
  fontSize: "20px",
  fontWeight: "bold",
};

const cardTextStyle = {
  margin: "5px 0",
};

const buttonGroupStyle = {
  marginTop: "10px",
  display: "flex",
  justifyContent: "space-between",
};

const buttonStyle = (color) => ({
  backgroundColor: color,
  color: "white",
  border: "none",
  padding: "8px 15px",
  borderRadius: "5px",
  cursor: "pointer",
  flex: 1,
  margin: "0 5px",
});

export default MedicineBookings;
