import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

// PaymentForm component
const PaymentForm = () => {
  const [amount, setAmount] = useState(0); // Default amount will be set from the cart
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);

  // Get the passed totalPrice from the Cart page
  const location = useLocation();
  const totalPrice = location.state?.totalPrice || 0;

  // Set the total amount to the price passed from the cart
  useEffect(() => {
    setAmount(totalPrice * 100); // Convert to cents
  }, [totalPrice]);

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();
    setProcessing(true);
  
    const token = localStorage.getItem('auth_token'); // Retrieve the token from localStorage
  
    if (!token) {
      setError('Authentication required. Please log in.');
      setProcessing(false);
      return;
    }
  
    try {
      const placeOrderResponse = await fetch('http://localhost:8000/medical_store_app/place-order/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`, // Add token to Authorization header
        },
        body: JSON.stringify({ amount: totalPrice, payment_method: 'COD' }),
      });
  
      if (placeOrderResponse.ok) {
        alert('Order placed successfully with COD');
      } else {
        const errorData = await placeOrderResponse.json();
        setError(errorData.error || 'Failed to place the order');
      }
    } catch (err) {
      setError('An error occurred while processing the order');
    }
  
    setProcessing(false);
  };
  
 

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Payment</h2>
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Amount:</label>
        <input
          type="number"
          value={amount / 100}
          readOnly
          style={styles.input}
        />
        <button
          type="submit"
          disabled={processing}
          style={
            processing
              ? { ...styles.button, ...styles.buttonDisabled }
              : styles.button
          }
        >
          {processing ? 'Processing...' : 'Place Order (COD)'}
        </button>
      </form>
      {error && <div style={styles.error}>{error}</div>}
    </div>
  );
};

// Main payment page component
const PaymentPage = () => <PaymentForm />;

// Styles
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
    fontFamily: 'Arial, sans-serif',
    width: '100%',
    maxWidth: '500px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    width: '100%',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonDisabled: {
    backgroundColor: '#ccc',
    cursor: 'not-allowed',
  },
  error: {
    color: 'red',
    fontSize: '14px',
    marginTop: '10px',
  },
};

export default PaymentPage;
