import React, { useState } from "react";
import NavBar from "../pages/NavBar"; // Import the NavBar component

const AdminDashboard = () => {
  const [totalCustomers, setTotalCustomers] = useState(1);
  const [totalBookings, setTotalBookings] = useState(0);
  const [totalProducts, setTotalProducts] = useState(13);

  const logout = () => {
    sessionStorage.clear();
    window.location.href = "/"; // Redirect after logout
  };

  const styles = {
    dashboard: {
      fontFamily: "'Poppins', sans-serif",
      padding: '20px',
      backgroundColor: '#f0f4f7',
      color: '#333',
      minHeight: '100vh'
    },
    header: {
      textAlign: 'center',
      padding: '20px',
      color: '#2a2a2a'
    },
    metrics: {
      display: 'flex',
      justifyContent: 'space-around',
      marginTop: '20px',
      padding: '20px',
      backgroundColor: '#fff',
      boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.1)',
      borderRadius: '8px'
    },
    metric: {
      padding: '10px 20px',
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      textAlign: 'center',
      color: '#333',
      boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
      minWidth: '150px'
    },
    metricTitle: {
      fontSize: '18px',
      fontWeight: '600',
      marginBottom: '5px'
    },
    metricValue: {
      fontSize: '24px',
      fontWeight: 'bold'
    }
  };

  return (
    <div style={styles.dashboard}>
      <NavBar userType="admin" onLogout={logout} /> {/* Use NavBar here */}
      <header style={styles.header}>
        <h1>Admin Dashboard</h1>
      </header>
      <div style={styles.metrics}>
        <div style={styles.metric}>
          <div style={styles.metricTitle}>Total Customers</div>
          <div style={styles.metricValue}>{totalCustomers}</div>
        </div>
        <div style={styles.metric}>
          <div style={styles.metricTitle}>Total Bookings</div>
          <div style={styles.metricValue}>{totalBookings}</div>
        </div>
        <div style={styles.metric}>
          <div style={styles.metricTitle}>Total Products</div>
          <div style={styles.metricValue}>{totalProducts}</div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
