import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

// Load Stripe outside of a componentâ€™s render to avoid recreating the object on every render
const stripePromise = loadStripe('your_stripe_publishable_key');

const PaymentForm = () => {
  const [clientSecret, setClientSecret] = useState('');
  const [amount, setAmount] = useState(0); // Default amount set via cart
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [paymentType, setPaymentType] = useState('card'); // Default payment type
  const [address, setAddress] = useState({
    street: '',
    city: '',
    state: '',
    zip: '',
  });

  const stripe = useStripe();
  const elements = useElements();

  const location = useLocation();
  const totalPrice = location.state?.totalPrice || 0;

  // Update the amount based on cart
  useEffect(() => {
    setAmount(totalPrice * 100); // Convert to cents
  }, [totalPrice]);

  // Fetch the client secret for Stripe payment intent
  useEffect(() => {
    const fetchClientSecret = async () => {
      if (amount > 0) {
        try {
          const response = await fetch('http://localhost:8000/create-payment-intent/', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ amount }),
          });
          const data = await response.json();
          if (response.ok) {
            setClientSecret(data.clientSecret);
          } else {
            setError(data.error || 'Failed to create payment intent');
          }
        } catch (err) {
          setError('Network error. Please try again.');
        }
      }
    };
    fetchClientSecret();
  }, [amount]);

  // Handle payment form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements || !clientSecret) return;

    setProcessing(true);

    if (paymentType === 'card') {
      const { error: stripeError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: elements.getElement(CardElement) },
      });

      if (stripeError) {
        setError(stripeError.message);
      } else if (paymentIntent.status === 'succeeded') {
        alert('Payment successful!');
      }
    } else {
      alert(`Payment method "${paymentType}" is not implemented yet.`);
    }

    setProcessing(false);
  };

  const handleAddressChange = (event) => {
    const { name, value } = event.target;
    setAddress((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Payment</h2>
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Payment Type:</label>
        <select
          value={paymentType}
          onChange={(e) => setPaymentType(e.target.value)}
          style={styles.input}
        >
          <option value="card">Card</option>
          <option value="paypal">PayPal</option>
          <option value="bank_transfer">Bank Transfer</option>
        </select>

        <label style={styles.label}>Amount:</label>
        <input type="number" value={amount / 100} readOnly style={styles.input} />

        <label style={styles.label}>Billing Address:</label>
        {['street', 'city', 'state', 'zip'].map((field) => (
          <input
            key={field}
            type="text"
            name={field}
            placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
            value={address[field]}
            onChange={handleAddressChange}
            style={styles.input}
          />
        ))}

        {paymentType === 'card' && <CardElement />}
        <button
          type="submit"
          disabled={processing || !clientSecret || !stripe}
          style={{
            ...styles.button,
            ...(processing || !stripe || !clientSecret ? styles.buttonDisabled : {}),
          }}
        >
          {processing ? 'Processing...' : 'Pay'}
        </button>
      </form>
      {error && <div style={styles.error}>{error}</div>}
    </div>
  );
};

const PaymentPage = () => (
  <Elements stripe={stripePromise}>
    <PaymentForm />
  </Elements>
);




const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
    fontFamily: 'Arial, sans-serif',
    width: '100%',
    maxWidth: '500px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    width: '100%',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonDisabled: {
    backgroundColor: '#ccc',
    cursor: 'not-allowed',
  },
  error: {
    color: 'red',
    fontSize: '14px',
    marginTop: '10px',
  },
};

export default PaymentPage;
