import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const PaymentForm = () => {
  const [address, setAddress] = useState('');
  const [isAddressEditing, setIsAddressEditing] = useState(false); // To toggle editing
  const navigate = useNavigate();

  const handleEditAddress = () => {
    // Navigate to address.js and pass the current address
    navigate("/address", { state: { address } });
  };

  const handleSaveAddress = (newAddress) => {
    setAddress(newAddress); // Update the address in the PaymentForm
    setIsAddressEditing(false);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Payment</h2>
      
      {/* Address Section */}
      <div style={styles.addressSection}>
        <label style={styles.label}>Delivery Address:</label>
        {isAddressEditing ? (
          <div>
            <input
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Enter your delivery address"
              style={styles.input}
            />
            <button
              onClick={() => handleSaveAddress(address)} // Save the address
              style={styles.button}
            >
              Save Address
            </button>
          </div>
        ) : (
          <div>
            <p>{address || 'No address provided'}</p>
            <button
              onClick={handleEditAddress} // Edit the address
              style={styles.button}
            >
              Edit Address
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
    fontFamily: 'Arial, sans-serif',
    width: '100%',
    maxWidth: '500px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  label: {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    width: '100%',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  error: {
    color: 'red',
    fontSize: '14px',
    marginTop: '10px',
  },
  addressSection: {
    width: '100%',
    marginBottom: '20px',
  },
};

export default PaymentForm;
