import React, { useState } from 'react';
import axios from 'axios';

const UpdateStockQuantity = () => {
  const [medicineId, setMedicineId] = useState('');
  const [stockQuantity, setStockQuantity] = useState('');
  const [message, setMessage] = useState('');

  const handleUpdate = async () => {
    if (!medicineId || !stockQuantity) {
      setMessage("Please enter both Medicine ID and Stock Quantity.");
      return;
    }

    try {
      const response = await axios.patch(
        `http://localhost:8000/medicine/update-stock/${medicineId}/`,
        { stock_quantity: stockQuantity },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`, // Ensure the user is authenticated
            'Content-Type': 'application/json',
          },
        }
      );
      setMessage(response.data.message || "Stock quantity updated successfully!");
    } catch (error) {
      if (error.response) {
        setMessage(error.response.data.error || "Failed to update stock quantity.");
      } else {
        setMessage("An error occurred. Please try again.");
      }
    }
  };

  return (
    <div>
      <h1>Update Stock Quantity</h1>
      <div>
        <label>Medicine ID:</label>
        <input
          type="text"
          value={medicineId}
          onChange={(e) => setMedicineId(e.target.value)}
          placeholder="Enter Medicine ID"
        />
      </div>
      <div>
        <label>Stock Quantity:</label>
        <input
          type="number"
          value={stockQuantity}
          onChange={(e) => setStockQuantity(e.target.value)}
          placeholder="Enter Stock Quantity"
        />
      </div>
      <button onClick={handleUpdate}>Update Stock</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default UpdateStockQuantity;
