import React, { useEffect, useState } from "react";
import { apiUrl, axiosInstance, generateRefreshToken } from "../axios";

export default function ListMedicines() {
  const [medicines, setMedicines] = useState([]);
  const [loading, setLoading] = useState(true);

  const deleteMedicine = async (id) => {
    await axiosInstance
      .delete(apiUrl + `/medical_store_app/medicine/${id}/`)
      .then((response) => {
        if (response.status === 200) {
          alert("Medicine deleted successfully!");
          // Instead of re-fetching the medicines, update the state directly
          setMedicines((prevMedicines) =>
            prevMedicines.filter((medicine) => medicine.id !== id)
          );
        }
      })
      .catch((error) => {
        console.error("Error deleting medicine:", error);
      });
  };

  const getMedicines = async () => {
    await axiosInstance
      .get(apiUrl + `/medical_store_app/medicine/`)
      .then((response) => {
        if (response.status === 200) {
          console.log("Medicines fetched:", response.data); 
          setMedicines(response.data);
          setLoading(false);
        }
      })
      .catch((error) => {
        if (error.response?.status === 401) {
          generateRefreshToken(error);
        } else {
          console.error("Error fetching medicines:", error);
        }
      });
  };
  function ProductRow({ medicine, onDelete, tdStyle, deleteButtonStyle }) {
    return (
      <tr>
        <td style={tdStyle}>{medicine.medicine_name || medicine.name}</td> 
        <td style={tdStyle}>{medicine.category || medicine.cat}</td> 
        <td style={tdStyle}>{medicine.price}</td>
        <td style={tdStyle}>{medicine.description}</td>
        <td style={tdStyle}>{medicine.stock_quantity || medicine.stock}</td> 
        <td style={tdStyle}>
          <button style={deleteButtonStyle} onClick={() => onDelete(medicine.id)}>
            Delete
          </button>
        </td>
      </tr>
    );
  }
  useEffect(() => {
    getMedicines();
  }, []);

  const tableStyle = {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "40px",
    fontFamily: "Arial, sans-serif",
  };
  
  const thStyle = {
    padding: "12px",
    backgroundColor: "#1976d2", 
    color: "white",
    textAlign: "left",
    borderBottom: "2px solid #ddd",
  };
  
  const tdStyle = {
    padding: "10px",
    borderBottom: "1px solid #ddd",
    textAlign: "left",
  };
  
  const deleteButtonStyle = {
    padding: "7px 14px",
    backgroundColor: "#ff5252",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
    transition: "background-color 0.3s ease",
  };
  
  // const deleteButtonHoverStyle = {
  //   ...deleteButtonStyle,
  //   backgroundColor: "#d32f2f", // Darker red on hover
  // };
  
  const containerStyle = {
    margin: "20px",
    fontFamily: "Arial, sans-serif",
  };
  
  const titleStyle = {
    textAlign: "center",
    marginBottom: "20px",
    color: "#333",
    fontSize: "24px",
  };
  
  // const noMedicineStyle = {
  //   textAlign: "center",
  //   padding: "20px",
  //   fontSize: "18px",
  //   color: "#888",
  // };
  
  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>View Medicines</h2>
      {loading ? (
        <p>Loading medicines...</p>
      ) : (
        <table style={tableStyle}>
          <thead>
            <tr>
              <th style={thStyle}>Medicine Name</th>
              <th style={thStyle}>Category</th>
              <th style={thStyle}>Price</th>
              <th style={thStyle}>Description</th>
              <th style={thStyle}>Stock Quantity</th>
              <th style={thStyle}>Action</th>
            </tr>
          </thead>
          <tbody>
            {medicines.length === 0 ? (
              <tr>
                <td colSpan="6" style={{ textAlign: "center" }}>No medicines available</td>
              </tr>
            ) : (
              medicines.map((medicine) => (
                <ProductRow
                  key={medicine.id} 
                  medicine={medicine}
                  onDelete={deleteMedicine} 
                  tdStyle={tdStyle}
                  deleteButtonStyle={deleteButtonStyle}
                />
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}

function ProductRow({ medicine, onDelete, tdStyle, deleteButtonStyle }) {
  return (
    <tr>
      <td style={tdStyle}>{medicine.medicine_name}</td>
      <td style={tdStyle}>{medicine.category}</td>
      <td style={tdStyle}>{medicine.price}</td>
      <td style={tdStyle}>{medicine.description}</td>
      <td style={tdStyle}>{medicine.stock_quantity}</td>
      <td style={tdStyle}>
        <button style={deleteButtonStyle} onClick={() => onDelete(medicine.id)}>
          Delete
        </button>
      </td>
    </tr>
  );
}
