import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
// import ''

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [paymentType, setPaymentType] = useState("Card");
  const [cardDetails, setCardDetails] = useState({
    cardNumber: "",
    cvv: "",
    expiryDate: "",
  });
  const [address, setAddress] = useState("");
  const [addressChanged, setAddressChanged] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCartData = async () => {
      try {
        const userId = sessionStorage.getItem("user_id");
        if (!userId) {
          alert("Please log in to view your cart.");
          navigate("/login");
          return;
        }
        const cartResponse = await axios.post("http://localhost:8000/medical_store_app/cart/", {
          user_id: userId,
        });

        if (cartResponse.data && cartResponse.data.cart_items) {
          setCartItems(cartResponse.data.cart_items);
          setTotalPrice(cartResponse.data.total_price);
        } else {
          console.error("Invalid cart data:", cartResponse.data);
        }

        const addressResponse = await axios.get(`http://localhost:8000/medical_store_app/get_address/${userId}`);
        if (addressResponse.data) {
          const { street, city, state, postal_code, phone_no } = addressResponse.data;
          const fullAddress = `${street}, ${city}, ${state} - ${postal_code}, ${phone_no}`;
          setAddress(fullAddress);
        } else {
          console.error("No address found for user:", userId);
          setAddress("");
        }
      } catch (error) {
        console.error("Error fetching data:", error);
        setAddress("");
      }
    };

    fetchCartData();
  }, [navigate]);

  const handlePaymentTypeChange = (e) => {
    setPaymentType(e.target.value);
    if (e.target.value !== "Card") {
      setCardDetails({ cardNumber: "", cvv: "", expiryDate: "" });
    }
  };

  const handleCardDetailsChange = (e) => {
    const { name, value } = e.target;
    setCardDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handlePayment = async () => {
    if (!address.trim()) {
      alert("Please add your billing address.");
      return;
    }
  
    const userId = sessionStorage.getItem("user_id");
    if (!userId) {
      alert("Please log in.");
      navigate("/login");
      return;
    }
  
    const paymentData = {
      payment_type: paymentType,
      user_id: userId,
      cart_items: cartItems,
      total_price: totalPrice,
    };
  
    if (paymentType === "Card") {
      const { cardNumber, cvv, expiryDate } = cardDetails;
      if (!cardNumber || !cvv || !expiryDate) {
        alert("Please fill in all card details.");
        return;
      }
      paymentData.card_details = cardDetails;
    }
  
    try {
      const response = await axios.post("http://localhost:8000/medical_store_app/process_payment/", paymentData, {
        headers: {
          "Content-Type": "application/json",
        },
      });
  
      if (response.data.status === "success") {
        alert(response.data.message);
        navigate("/UserProductViews");
      } else {
        alert("Payment failed: " + response.data.error);
      }
    } catch (error) {
      console.error("Payment error:", error);
      alert("An error occurred during payment.");
    }
  };
  


  return (
    <div className="checkout-container">
      <h1 className="checkout-heading">Checkout</h1>
      <div className="checkout-main-box">
        <div className="checkout-address-box">
          <h2 className="checkout-sub-heading">Billing Address</h2>
          {address.trim() ? (
            <div>
              <textarea
                className="checkout-textarea"
                value={address}
                disabled={!addressChanged}
                onChange={(e) => setAddress(e.target.value)}
              />
              <button
                className="checkout-button"
                onClick={() => setAddressChanged(true)}
              >
                Change Address
              </button>
              {addressChanged && (
                <button
                  className="checkout-button"
                  onClick={() => alert("Address saving feature is under development")}
                >
                  Save Address
                </button>
              )}
            </div>
          ) : (
            <div>
              <p>Please add your billing address.</p>
              <button className="checkout-button" onClick={() => navigate("/addaddress")}>
                Add Address
              </button>
            </div>
          )}
        </div>

        <div className="checkout-payment-box">
          <h2 className="checkout-sub-heading">Cart Details</h2>
          <table className="checkout-table">
            <thead>
              <tr>
                <th>Medicine</th>
                <th>Quantity</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, index) => (
                <tr key={index}>
                  <td>{item.medicine?.name || "Unknown Medicine"}</td>
                  <td>{item.quantity || 0}</td>
                  <td>
                    Rs.
                    {item.medicine?.price && item.quantity
                      ? item.medicine.price * item.quantity
                      : 0}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <h3 className="checkout-total">Total: Rs.{totalPrice}</h3>

          <h2 className="checkout-sub-heading">Payment Details</h2>
          <div className="checkout-payment-type-container">
            <label>
              <input
                type="radio"
                value="Card"
                checked={paymentType === "Card"}
                onChange={handlePaymentTypeChange}
              />
              Pay by Card
            </label>
            {paymentType === "Card" && (
              <div className="checkout-card-details-container">
                <input
                  className="checkout-card-input"
                  type="text"
                  placeholder="Card Number"
                  name="cardNumber"
                  value={cardDetails.cardNumber}
                  onChange={handleCardDetailsChange}
                />
                <input
                  className="checkout-card-input"
                  type="text"
                  placeholder="CVV"
                  name="cvv"
                  value={cardDetails.cvv}
                  onChange={handleCardDetailsChange}
                />
                <input
                  className="checkout-card-input"
                  type="text"
                  placeholder="MM/YYYY"
                  name="expiryDate"
                  value={cardDetails.expiryDate}
                  onChange={handleCardDetailsChange}
                />
              </div>
            )}
            <label>
              <input
                type="radio"
                value="Cash"
                checked={paymentType === "Cash"}
                onChange={handlePaymentTypeChange}
              />
              Cash on Delivery
            </label>
          </div>
          <button className="checkout-payment-button" onClick={handlePayment}>
            {paymentType === "Card" ? "Confirm Payment" : "Place Order"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
