import React, { useEffect, useState, useCallback, useMemo } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [isFileUploaded, setIsFileUploaded] = useState(false); // Track file upload
  const navigate = useNavigate();

  const user_data = useMemo(
    () => ({ user_id: sessionStorage.getItem("user_id") }),
    []
  );

  const fetchCart = useCallback(async () => {
    if (!user_data.user_id) {
      console.error("No user ID found in session.");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/cart/",
        user_data
      );

      console.log("Cart response:", response.data);
      const { cart_items, total_price } = response.data;

      setCartItems(cart_items || []);
      setTotalPrice(total_price || 0);

      // Save cart data locally as a backup
      localStorage.setItem("cart", JSON.stringify(cart_items));
      localStorage.setItem("totalPrice", total_price);
    } catch (error) {
      console.error("Error fetching cart items:", error);

      // Load from localStorage if API fails
      const localCart = JSON.parse(localStorage.getItem("cart")) || [];
      const localTotalPrice = localStorage.getItem("totalPrice") || 0;

      console.log("Loading cart from local backup.");
      setCartItems(localCart);
      setTotalPrice(localTotalPrice);
    }
  }, [user_data]);

  useEffect(() => {
    console.log("Fetching cart...");
    fetchCart();
  }, [fetchCart]);

  const handleRemoveFromCart = async (cartId) => {
    try {
      await axios.post(
        `http://localhost:8000/medical_store_app/cart/remove/${cartId}/${sessionStorage.getItem(
          "user_id"
        )}`
      );
      fetchCart(); 
    } catch (error) {
      console.error("Error removing cart item:", error);
    }
  };

  const handleProceedToCheckout = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/create_order/",
        user_data
      );

      console.log("Order response:", response.data);
      setCartItems([]);
      setTotalPrice(0);
      localStorage.removeItem("cart");
      localStorage.removeItem("totalPrice");

      console.log("Order placed successfully! Cart cleared.");

      navigate("/checkout", {
        state: { order: response.data.order, totalPrice: totalPrice },
      });
    } catch (error) {
      console.error("Error booking order:", error);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) {
      alert("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("prescription_file", file);
    formData.append("user", sessionStorage.getItem("user_id")); // Attach user ID

    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/upload_prescription/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      console.log("File upload response:", response.data);
      alert("File uploaded successfully!");
      setIsFileUploaded(true); // Enable "Proceed to Booking" button
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Failed to upload the file. Please try again.");
    }
  };

  const handleContinueShopping = () => {
    navigate("/UserProductViews");
  };

  return (
    <div style={styles.container}>
      <button
        style={styles.continueShoppingButton}
        onClick={handleContinueShopping}
      >
        Continue Shopping
      </button>
      <h1 style={styles.heading}>Your Cart</h1>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.tableHeader}>Medicine</th>
            <th style={styles.tableHeader}>Quantity</th>
            <th style={styles.tableHeader}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td>{item.medicine.name}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  onClick={() => handleRemoveFromCart(item.id)}
                  style={styles.button}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 style={styles.total}>Total: â‚¹{totalPrice}</h3>
      <div style={styles.uploadContainer}>
        <label htmlFor="prescription" style={styles.uploadLabel}>
          Upload Prescription:
        </label>
        <input
          type="file"
          id="prescription"
          onChange={handleFileUpload}
          style={styles.fileInput}
        />
      </div>
      <div style={styles.BookContainer}>
        <button
          style={{
            ...styles.paymentButton,
            opacity: isFileUploaded ? 1 : 0.5,
            cursor: isFileUploaded ? "pointer" : "not-allowed",
          }}
          onClick={handleProceedToCheckout}
          disabled={!isFileUploaded}
        >
          Proceed to Booking
        </button>
      </div>
    </div>
  );
};

export default Cart;
