import React, { useEffect, useState, useCallback, useMemo } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const navigate = useNavigate();

  const user_data = useMemo(() => ({ user_id: sessionStorage.getItem("user_id") }), []);

  // Function to refresh the token
  const refreshAuthToken = async () => {
    try {
      const refreshToken = sessionStorage.getItem("refresh_token");
      if (!refreshToken) return null;

      const response = await axios.post("http://localhost:8000/api/token/refresh/", { refresh: refreshToken });
      sessionStorage.setItem("auth_token", response.data.access);
      sessionStorage.setItem("refresh_token", response.data.refresh);
      return response.data.access;
    } catch (error) {
      console.error("Error refreshing token", error);
      return null;
    }
  };

  // Function to fetch cart items
  const fetchCart = useCallback(async () => {
    const token = sessionStorage.getItem("auth_token");
    if (!token || !user_data.user_id) return;

    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/cart/",
        user_data,
        {
          headers: {
            "Authorization": `Bearer ${token}`
          }
        }
      );
      const { cart_items, total_price } = response.data;
      setCartItems(cart_items || []);
      setTotalPrice(total_price || 0);
    } catch (error) {
      if (error.response?.data?.code === "token_not_valid") {
        // Try refreshing the token if it's expired
        const newToken = await refreshAuthToken();
        if (newToken) {
          fetchCart(); // Retry fetching cart after refreshing token
        } else {
          alert("Your session has expired. Please log in again.");
          navigate("/login"); // Redirect to login page
        }
      } else {
        // Fallback to localStorage if API fails
        const localCart = JSON.parse(localStorage.getItem("cart")) || [];
        const localTotalPrice = localStorage.getItem("totalPrice") || 0;
        setCartItems(localCart);
        setTotalPrice(localTotalPrice);
      }
    }
  }, [user_data, navigate]);

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  // Function to remove an item from the cart
  const handleRemoveFromCart = async (cartId) => {
    const token = sessionStorage.getItem("auth_token");
    if (!token) return;

    try {
      await axios.post(
        `http://localhost:8000/medical_store_app/cart/remove/${cartId}/${sessionStorage.getItem("user_id")}`,
        {},
        {
          headers: {
            "Authorization": `Bearer ${token}`
          }
        }
      );
      fetchCart(); // Refresh cart after removing item
    } catch (error) {
      console.error("Error removing item from cart", error);
    }
  };

  // Proceed to checkout
  const handleProceedToCheckout = async () => {
    const token = sessionStorage.getItem("auth_token");
    if (!token) return;

    try {
      const response = await axios.post(
        "http://localhost:8000/medical_store_app/create_order/",
        user_data,
        {
          headers: {
            "Authorization": `Bearer ${token}`
          }
        }
      );
      setCartItems([]);
      setTotalPrice(0);
      navigate("/checkout", {
        state: { order: response.data.order, totalPrice: totalPrice },
      });
    } catch (error) {
      console.error("Error proceeding to checkout", error);
    }
  };

  // Handle file upload
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("prescription_file", file);
    formData.append("user", sessionStorage.getItem("user_id"));

    const token = sessionStorage.getItem("auth_token");
    if (!token) return;

    try {
      await axios.post(
        "http://localhost:8000/medical_store_app/upload_prescription/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            "Authorization": `Bearer ${token}`,
          },
        }
      );
      setIsFileUploaded(true);
    } catch (error) {
      console.error("Error uploading file", error);
      if (error.response?.data?.code === "token_not_valid") {
        const newToken = await refreshAuthToken();
        if (newToken) {
          handleFileUpload(event); // Retry file upload with new token
        } else {
          alert("Your session has expired. Please log in again.");
          navigate("/login"); // Redirect to login page
        }
      }
    }
  };

  // Continue shopping
  const handleContinueShopping = () => {
    navigate("/UserProductViews");
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <button
        style={{ padding: "10px 20px", marginBottom: "20px", cursor: "pointer" }}
        onClick={handleContinueShopping}
      >
        Continue Shopping
      </button>
      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>Your Cart</h1>
      <table style={{ width: "100%", borderCollapse: "collapse", marginBottom: "20px" }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Medicine</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Quantity</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, index) => (
            <tr key={index}>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>{item.medicine.name}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>{item.quantity}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                <button
                  onClick={() => handleRemoveFromCart(item.id)}
                  style={{ padding: "5px 10px", cursor: "pointer" }}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3 style={{ textAlign: "right", marginBottom: "20px" }}>Total: â‚¹{totalPrice}</h3>
      <div style={{ marginBottom: "20px" }}>
        <label htmlFor="prescription" style={{ marginRight: "10px" }}>Upload Prescription:</label>
        <input
          type="file"
          id="prescription"
          onChange={handleFileUpload}
          style={{ padding: "5px" }}
        />
      </div>
      <button
        style={{
          padding: "10px 20px",
          cursor: isFileUploaded ? "pointer" : "not-allowed",
          opacity: isFileUploaded ? 1 : 0.5,
        }}
        onClick={handleProceedToCheckout}
        disabled={!isFileUploaded}
      >
        Proceed to Booking
      </button>
    </div>
  );
};

export default Cart;
