import React, { useState } from 'react';
import axios from 'axios';

const Booking = () => {
  const [items, setItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const handleAddItem = () => {
    setItems([...items, { medicine_id: '', quantity: '' }]);
  };

  const handleItemChange = (index, field, value) => {
    const updatedItems = [...items];
    updatedItems[index][field] = value;
    setItems(updatedItems);
  };

  const handleRemoveItem = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };

  const handleSubmit = async () => {
    setError('');
    setSuccess(false);
  
    if (!totalPrice || items.length === 0) {
      setError('Please provide a total price and at least one item.');
      return;
    }
  
    const invalidItems = items.some(
      (item) => !item.medicine_id || !item.quantity || item.quantity <= 0
    );
  
    if (invalidItems) {
      setError('Each item must have a valid medicine ID and quantity.');
      return;
    }
  
    try {
      const data = { total_price: parseFloat(totalPrice), items };
      const response = await axios.post('http://localhost:8000/medical_store_app/book_order/', data, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      setSuccess(true);
      console.log('Order created successfully:', response.data);
    } catch (error) {
      setError(error.response?.data?.error || 'Something went wrong.');
      console.error(error);
    }
  };
  

  return (
    <div className="booking-container">
      <h1>Book an Order</h1>
      {success && <p className="success-message">Order placed successfully!</p>}
      {error && <p className="error-message">{error}</p>}

      <div className="form-group">
        <label>Total Price:</label>
        <input
          type="number"
          value={totalPrice}
          onChange={(e) => setTotalPrice(e.target.value)}
        />
      </div>

      <h3>Items</h3>
      {items.map((item, index) => (
        <div key={index} className="item-row">
          <div className="form-group">
            <label>Medicine ID:</label>
            <input
              type="text"
              value={item.medicine_id}
              onChange={(e) =>
                handleItemChange(index, 'medicine_id', e.target.value)
              }
            />
          </div>
          <div className="form-group">
            <label>Quantity:</label>
            <input
              type="number"
              value={item.quantity}
              onChange={(e) =>
                handleItemChange(index, 'quantity', e.target.value)
              }
            />
          </div>
          <button onClick={() => handleRemoveItem(index)}>Remove</button>
        </div>
      ))}
      <button onClick={handleAddItem}>Add Item</button>

      <button onClick={handleSubmit} className="submit-btn">
        Place Order
      </button>
    </div>
  );
};

export default Booking;
