import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiUrl } from "../axios";

export default function Register() {
  const [formValues, setFormValues] = useState({});
  const [formError, setFormError] = useState({});
  const navigate = useNavigate();

  const onChangeHandler = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });

    if (name === "cpassword") {
      setFormError({
        ...formError,
        password: value !== formValues.password ? "*Passwords do not match" : "",
      });
    } else if (name === "password" && formValues.cpassword) {
      setFormError({
        ...formError,
        password: formValues.cpassword !== value ? "*Passwords do not match" : "",
      });
    }
  };

  const submit = async (e) => {
    e.preventDefault();
    if (Object.keys(formError).length > 0) {
      alert("Please fix the errors before submitting.");
      return;
    }

    try {
      const response = await axios.post(`${apiUrl}/user/`, formValues);
      if (response.status === 200) {
        alert("Registration successful!");
        navigate("/login");
      }
    } catch (error) {
      console.error("Registration error:", error);
      alert("Registration failed. Please try again.");
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)",
      }}
    >
      <div
        className="container mt-5"
        style={{
          width: "550px",
          height: "auto",
          boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)",
          backgroundColor: "#ffffff",
          padding: "40px",
          borderRadius: "15px",
          border: "2px solid #007bff",
        }}
      >
        <form onSubmit={submit} method="post" className="form">
          <div className="row mt-3 mb-3">
            <h2 className="text-center" style={{ fontSize: "30px", color: "#007bff" }}>
              <i className="bi bi-person-plus px-3"></i>Register
            </h2>
          </div>

          {/* First Name */}
          <div className="row mb-4">
            <div className="col">
              <label
                htmlFor="first_name"
                className="form-label"
                style={{ fontSize: "17px", color: "#333", marginBottom: "10px" }} // Adjusted margin
              >
              First Name:    
              </label>
              <input
                required
                type="text"
                name="first_name"
                id="first_name"
                placeholder= "Enter your first name"
                className="form-control"
                style={{
                  fontSize: "16px",
                  padding: "8px",
                  border: "1px solid #ccc",
                  borderRadius: "9px",
                  marginBottom:"10px"
                }}
                onChange={onChangeHandler}
              />
            </div>
          </div>

          {/* Last Name */}
          <div className="row mb-4">
            <div className="col">
              <label
                htmlFor="last_name"
                className="form-label"
                style={{ fontSize: "17px", color: "#333", marginBottom: "10px" }}
              >
                Last Name
              </label>
              <input
                type="text"
                name="last_name"
                id="last_name"
                placeholder="Enter your last name"
                className="form-control"
                style={{
                  fontSize: "16px",
                  padding: "10px",
                  border: "1px solid #ccc",
                  borderRadius: "8px",
                  marginBottom:"10px"
                }}
                onChange={onChangeHandler}
              />
            </div>
          </div>

          {/* Username */}
          <div className="row mb-4">
            <div className="col">
              <label
                htmlFor="username"
                className="form-label"
                style={{ fontSize: "17px", color: "#333", marginBottom: "10px" }}
              >
                Username
              </label>
              <input
                required
                type="text"
                name="username"
                id="username"
                placeholder="Enter your username"
                className="form-control"
                style={{
                  fontSize: "16px",
                  padding: "10px",
                  border: "1px solid #ccc",
                  borderRadius: "8px",
                  marginBottom:"10px"
                }}
                onChange={onChangeHandler}
              />
            </div>
          </div>

          {/* Mobile Number */}
          <div className="row mb-4">
            <div className="col">
              <label
                htmlFor="mobile"
                className="form-label"
                style={{ fontSize: "17px", color: "#333", marginBottom: "10px" }}
              >
                Mobile Number
              </label>
              <input
                required
                type="text"
                name="mobile_no"
                id="mobile"
                placeholder="Enter your mobile number"
                pattern="[6789][0-9]{9}"
                title="Please enter a valid phone number"
                className="form-control"
                style={{
                  fontSize: "16px",
                  padding: "10px",
                  border: "1px solid #ccc",
                  borderRadius: "8px",
                  marginBottom:"10px"
                }}
                onChange={onChangeHandler}
              />
            </div>
          </div>

          {/* Email */}
          <div className="row mb-4">
            <div className="col">
              <label
                htmlFor="email"
                className="form-label"
                style={{ fontSize: "17px", color: "#333", marginBottom: "10px" }}
              >
                Email
              </label>
              <input
                required
                type="email"
                name="email"
                id="email"
                placeholder="Enter your email"
                className="form-control"
                style={{
                  fontSize: "16px",
                  padding: "10px",
                  border: "1px solid #ccc",
                  borderRadius: "8px",
                  marginBottom:"10px"
                }}
                onChange={onChangeHandler}
              />
            </div>
          </div>

          {/* Password */}
          <div className="row mb-4">
            <div className="col">
              <label
                htmlFor="password"
                className="form-label"
                style={{ fontSize: "17px", color: "#333", marginBottom: "10px" }}
              >
                Password
              </label>
              <input
                required
                type="password"
                className="form-control"
                name="password"
                id="password"
                placeholder="Enter your password"
                pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}"
                title="Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters"
                style={{
                  fontSize: "16px",
                  padding: "10px",
                  border: "1px solid #ccc",
                  borderRadius: "8px",
                  marginBottom:"10px"
                }}
                onChange={onChangeHandler}
              />
            </div>
          </div>

          {/* Confirm Password */}
          <div className="row mb-4">
            <div className="col">
              <label
                htmlFor="cpassword"
                className="form-label"
                style={{ fontSize: "17px", color: "#333", marginBottom: "10px" }}
              >
                Confirm Password
              </label>
              <input
                required
                type="password"
                name="cpassword"
                placeholder="Confirm your password"
                className="form-control"
                id="cpassword"
                style={{
                  fontSize: "16px",
                  padding: "10px",
                  border: "1px solid #ccc",
                  borderRadius: "8px",
                  marginBottom:"10px"
                }}
                onChange={onChangeHandler}
              />
              <p className="text-danger" style={{ fontSize: "14px" }}>{formError.password}</p>
            </div>
          </div>

          {/* Submit Button */}
          <div className="text-center">
            <button
              type="submit"
              className="btn btn-dark"
              style={{
                width: "100%",
                fontSize: "17px",
                padding: "10px 20px",
                backgroundColor: "#007bff",
                border: "none",
                borderRadius: "8px",
              }}
            >
              Register
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
