import React, { useEffect, useState } from "react";
import axios from "axios";

const MedicineBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchBookings = async () => {
    try {
      const { data } = await axios.get("http://localhost:8000/medical_store_app/payment_details/");
      if (Array.isArray(data.paymentDetails)) {
        setBookings(
          data.paymentDetails.map(({ user, razorpay_order_id, amount, razorpay_payment_id, status }) => ({
            user: user || "Unknown",
            id: razorpay_order_id || "Unknown",
            amount: amount || "0.00",
            paymentId: razorpay_payment_id || "N/A",
            status: status || "Unknown",
          }))
        );
      } else {
        throw new Error("Invalid response format");
      }
    } catch (err) {
      console.error("Error fetching bookings:", err);
      setError(err.message || "Failed to fetch bookings.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBookings();
    const interval = setInterval(fetchBookings, 5000); // Poll every 5 seconds
    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []);

  const styles = {
    container: {
      margin: "0 auto",
      padding: "80px",
      maxWidth: "1200px",
      textAlign: "center",
    },
    table: {
      border: "1px solid black",
      width: "100%",
      borderCollapse: "collapse",
      marginTop: "20px",
    },
    header: {
      backgroundColor: "#f2f2f2",
      fontWeight: "bold",
      textAlign: "left",
      border: "1px solid black",
      padding: "8px",
    },
    cell: {
      border: "1px solid black",
      padding: "8px",
    },
    emptyRow: {
      textAlign: "center",
      fontStyle: "italic",
      padding: "16px",
    },
    errorText: {
      color: "red",
    },
  };

  return (
    <div style={styles.container}>
      <h2>Medicine Bookings</h2>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p style={styles.errorText}>{error}</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.header}>User</th>
              <th style={styles.header}>Order ID</th>
              <th style={styles.header}>Amount</th>
              <th style={styles.header}>Payment ID</th>
              <th style={styles.header}>Status</th>
            </tr>
          </thead>
          <tbody>
            {bookings.length === 0 ? (
              <tr>
                <td colSpan="5" style={styles.emptyRow}>
                  No bookings found
                </td>
              </tr>
            ) : (
              bookings.map((booking, index) => (
                <tr key={index}>
                  <td style={styles.cell}>{booking.user}</td>
                  <td style={styles.cell}>{booking.id}</td>
                  <td style={styles.cell}>{booking.amount}</td>
                  <td style={styles.cell}>{booking.paymentId}</td>
                  <td style={styles.cell}>{booking.status}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default MedicineBookings;
