import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { cartItems, totalPrice, clearCart } = location.state;

  useEffect(() => {
    if (!cartItems || !totalPrice) {
      console.error("Missing cart items or total price.");
      return;
    }

    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";

    script.onload = () => {
      const options = {
        key: "rzp_test_a6hVvFHJbT9SAO",
        amount: totalPrice * 100,
        currency: "INR",
        name: "Medical Store",
        description: "Order Payment",
        handler: async (response) => {
          const paymentData = {
            razorpay_payment_id: response.razorpay_payment_id,
            amount: totalPrice * 100,
          };
          try {
            await axios.post("http://localhost:8000/medical_store_app/payment_details/", paymentData);
            alert("Payment successful!");

            // Clear the cart and navigate to a confirmation page
            clearCart();
            navigate("/confirmation", { state: { paymentData } });
          } catch (error) {
            console.error("Error processing payment:", error);
            alert("Payment failed. Please try again.");
          }
        },
        prefill: { name: "Customer Name", email: "customer@example.com", contact: "9999999999" },
        theme: { color: "#F37254" },
      };
      const razorpay = new window.Razorpay(options);
      razorpay.open();
    };

    script.onerror = () => {
      alert("Failed to load Razorpay script.");
    };

    document.body.appendChild(script);
    return () => document.body.removeChild(script);
  }, [cartItems, totalPrice, clearCart, navigate]);

  if (!cartItems || !totalPrice) {
    return <div style={styles.error}>Error: Missing order details or total price.</div>;
  }

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Checkout</h2>
      <p style={styles.message}>Please wait while your payment is processed...</p>
    </div>
  );
};

const styles = {
  container: { textAlign: "center", padding: "20px" },
  heading: { fontSize: "24px", marginBottom: "20px" },
  message: { fontSize: "18px" },
  error: { color: "red", fontSize: "18px", textAlign: "center" },
};

export default Checkout;
