import React, { useState, useEffect } from 'react';

function Product() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch product data from Django API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/api/products/'); // Adjust URL if needed
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Inline CSS Styles
  const styles = {
    container: {
      padding: '20px',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
      gap: '20px',
    },
    card: {
      border: '1px solid #ccc',
      padding: '10px',
      borderRadius: '8px',
      backgroundColor: '#f9f9f9',
      textAlign: 'center',
    },
    productName: {
      fontSize: '1.2em',
    },
    button: (inStock) => ({
      backgroundColor: inStock ? '#28a745' : '#ccc',
      color: 'white',
      padding: '10px',
      border: 'none',
      cursor: inStock ? 'pointer' : 'not-allowed',
      borderRadius: '4px',
    }),
  };

  // Render Loading, Error, or Product List
  if (loading) return <div style={styles.container}>Loading...</div>;
  if (error) return <div style={styles.container}>Error: {error}</div>;

  return (
    <div style={styles.container}>
      <h1>Available Products</h1>
      <div style={styles.grid}>
        {products.map((product) => (
          <div key={product.id} style={styles.card}>
            <h2 style={styles.productName}>{product.name}</h2>
            <p>{product.description}</p>
            <p><strong>Price: </strong>${product.price}</p>
            <p><strong>Stock: </strong>{product.in_stock ? 'In Stock' : 'Out of Stock'}</p>
            <button
              style={styles.button(product.in_stock)}
              disabled={!product.in_stock}
            >
              {product.in_stock ? 'Buy Now' : 'Out of Stock'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default product;
