import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiUrl } from "../axios"; // Ensure this is correctly imported
import { jwtDecode } from "jwt-decode"; // Change here

export default function Login() {
  const navigate = useNavigate();
  const [formValues, setFormValues] = useState({});
  const [formError, setFormError] = useState("");

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    axios
      .post(apiUrl + "/api/token/", formValues)
      .then((response) => {
        sessionStorage.setItem("access_token", response.data.access);
        sessionStorage.setItem("refresh_token", response.data.refresh);
        const user = jwtDecode(response.data.access); // Change here
        const userId = user.user_id;
        const username = user.name;
        const userType = user.user_type;

        axios.defaults.headers.common["Authorization"] = `Bearer ${response.data.access}`;
        sessionStorage.setItem("user_id", userId);
        sessionStorage.setItem("name", username);
        sessionStorage.setItem("user_type", userType);

        if (userType === "admin") navigate("/admin/users");
        else if (userType === "doctor") navigate("/doctor/dashboard"); // New route for doctors
        else if (userType === "patient") navigate("/patient/appointments"); // New route for patients
      })
      .catch((error) => {
        if (error.response && error.response.status === 401) {
          setFormError(error.response.data.detail);
        }
      });
  };

  return (
    <div>
      
        <form onSubmit={onSubmit} method="post" className="form">
          <div className="row mt-3 mb-3">
            <h4 className="text-center">
              <i className="bi bi-person-circle px-3"></i>Login
            </h4>
          </div>
          <div className="row mb-3">
            <div className="col">
              <label htmlFor="" className="form-label">
                Username
              </label>
              <input
                type="text"
                name="username"
                placeholder="username"
                className="form-control"
                onChange={onChangeHandler}
              />
            </div>
          </div>
          <div className="row mb-3">
            <div className="col">
              <label htmlFor="" className="form-label">
                Password
              </label>
              <input
                type="password"
                name="password"
                placeholder="password"
                className="form-control"
                onChange={onChangeHandler}
              />
            </div>
          </div>
          <p className="text-danger">{formError}</p>
          <p>
            New User? <a href="register">Register here</a>
            <br />
          </p>
          <div className="row mb-3">
            <div className="col">
              <button className="btn btn-dark col-3">Login</button>
            </div>
          </div>
        </form>
      </div>
    // </div>
  );
}
