// Checkout.js
import React, { useEffect } from "react";
import { useLocation } from "react-router-dom";

const Checkout = () => {
  const location = useLocation();
  const { order, totalPrice } = location.state;

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.onload = () => {
      initializeRazorpay();
    };
    script.onerror = () => {
      alert("Failed to load Razorpay script.");
    };
    document.body.appendChild(script);
  }, []);

  const initializeRazorpay = () => {
    debugger;
    const options = {
      key: "rzp_test_a6hVvFHJbT9SAO", 
      amount: totalPrice * 100, // Convert amount to paise
      currency: "INR",
      name: "Your Company Name",
      description: "Payment for Order #" + order.id,
      order_id: order.id, // Order ID from your backend
      handler: function (response) {
        console.log("Payment successful", response);
        alert("Payment successful");
      },
      prefill: {
        name: "Customer Name",
        email: "customer@example.com",
        contact: "1234567890",
      },
      notes: {
        address: "Customer Address",
      },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div>
      <h2>Checkout</h2>
      <p>Total: â‚¹{totalPrice}</p>
      <button onClick={initializeRazorpay}>Pay Now</button>
    </div>
  );
};

export default Checkout;
