import React from "react";
// Uncomment the line below if AuthHandler is needed
import AuthHandler from "../utils/AuthHandler";
import APIHandler from "../utils/APIHandler"; // Ensure this path is correct
import { Link } from "react-router-dom";

class MedicineAdd extends React.Component {
  constructor(props) {
    super(props);
    this.formSubmit = this.formSubmit.bind(this);
  }
  
  state = {
    errorRes: false,
    errorMessage: "",
    btnMessage: 0,
    sendData: false,
    loading: false, // Added loading state
    companylist: [],
    medicinedetails: [
      { salt_name: "", salt_qty: "", salt_qty_type: "", description: "" },
    ],
  };

  async formSubmit(event) {
    event.preventDefault();
    this.setState({ btnMessage: 1 });

    var apiHandler = new APIHandler();
    try {
      var response = await apiHandler.saveMedicineData(
        event.target.name.value,
        event.target.medical_typ.value,
        event.target.buy_price.value,
        event.target.sell_price.value,
        event.target.c_gst.value,
        event.target.s_gst.value,
        event.target.batch_no.value,
        event.target.shelf_no.value,
        event.target.expire_date.value,
        event.target.mfg_date.value,
        event.target.company_id.value,
        event.target.description1.value,
        event.target.in_stock_total.value,
        event.target.qty_in_strip.value,
        this.state.medicinedetails
      );

      this.setState({
        errorRes: response.data.error,
        errorMessage: response.data.message,
        sendData: true,
      });
    } catch (error) {
      console.error("Error saving medicine data:", error);
      this.setState({
        errorRes: true,
        errorMessage: "An error occurred while saving the data.",
        sendData: true,
      });
    } finally {
      this.setState({ btnMessage: 0 });
    }
  }

  componentDidMount() {
    this.LoadCompany();
  }

  async LoadCompany() {
    this.setState({ loading: true }); // Start loading
    var apiHandler = new APIHandler();
    try {
      var companydata = await apiHandler.fetchCompanyOnly();
      this.setState({ companylist: companydata.data });
    } catch (error) {
      console.error("Error fetching company data:", error);
    } finally {
      this.setState({ loading: false }); // End loading
    }
  }

  RemoveItems = () => {
    if (this.state.medicinedetails.length !== 1) {
      const updatedMedicinedetails = this.state.medicinedetails.slice(0, -1);
      this.setState({ medicinedetails: updatedMedicinedetails });
    }
  };

  handleInput = (event) => {
    const keyname = event.target.name;
    const value = event.target.value;
    const index = event.target.getAttribute("data-index");
    const updatedMedicinedetails = [...this.state.medicinedetails];
    updatedMedicinedetails[index][keyname] = value;
    this.setState({ medicinedetails: updatedMedicinedetails });
  };

  AddItem = () => {
    const newItem = {
      salt_name: "",
      salt_qty: "",
      salt_qty_type: "",
      description: "",
    };

    this.setState(prevState => ({
      medicinedetails: [...prevState.medicinedetails, newItem]
    }));
  };

  render() {
    // Inner CSS styles
    const styles = {
      container: {
        padding: "20px",
        backgroundColor: "#f9f9f9",
        borderRadius: "5px",
      },
      header: {
        marginBottom: "20px",
      },
      formControl: {
        marginBottom: "15px",
      },
      button: {
        width: "100%",
      },
      alertSuccess: {
        color: "green",
      },
      alertDanger: {
        color: "red",
      },
    };

    return (
      <section className="content">
        <div className="container-fluid">
          <div className="block-header">
            <h2 style={styles.header}>ADD Medicine</h2>
          </div>
          <div className="row clearfix">
            <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
              <div className="card" style={styles.container}>
                <div className="header">
                  <h2>Add Medicine</h2>
                </div>
                <div className="body">
                  <form onSubmit={this.formSubmit}>
                    <label htmlFor="email_address">Name</label>
                    <div className="form-group" style={styles.formControl}>
                      <div className="form-line">
                        <input
                          type="text"
                          id="name"
                          name="name"
                          className="form-control"
                          placeholder="Enter Name"
                        />
                      </div>
                    </div>
                    {/* ...rest of your form fields... */}
                    <button
                      type="submit"
                      className="btn btn-primary m-t-15 waves-effect"
                      style={styles.button}
                      disabled={this.state.btnMessage === 1}
                    >
                      {this.state.btnMessage === 0
                        ? "Add Medicine"
                        : "Adding Medicine Please Wait.."}
                    </button>
                    <br />
                    {this.state.errorRes === false && this.state.sendData === true ? (
                      <div className="alert alert-success" style={styles.alertSuccess}>
                        <strong>Success!</strong> {this.state.errorMessage}.
                      </div>
                    ) : (
                      ""
                    )}
                    {this.state.errorRes === true && this.state.sendData === true ? (
                      <div className="alert alert-danger" style={styles.alertDanger}>
                        <strong>Failed!</strong> {this.state.errorMessage}.
                      </div>
                    ) : (
                      ""
                    )}
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default MedicineAdd; // Correct the export statement to match the class name
