import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiUrl } from "../axios";
import { jwtDecode } from "jwt-decode"; // Corrected import as named import

export default function Login() {
  const navigate = useNavigate();
  const [formValues, setFormValues] = useState({ username: "", password: "" });
  const [formError, setFormError] = useState("");
  const [loading, setLoading] = useState(false);

  const onChangeHandler = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    axios
      .post(`${apiUrl}/medicalstoreapp/api/token/`, formValues) // Use POST for login
      .then((response) => {
        console.log("Response Data:", response.data); // Debugging response
        // Handle successful login
        sessionStorage.setItem("access_token", response.data.token); // Assuming your backend returns 'token'
        sessionStorage.setItem("refresh_token", response.data.refresh); // Assuming your backend returns 'refresh'

        // Decode the JWT to extract user information
        const user = jwtDecode(response.data.token); // Decode the JWT
        const userId = user.user_id; // Ensure these fields match your token's payload
        const username = user.username;
        const user_type = user.user_type;

        // Set Authorization header for future requests
        axios.defaults.headers.common["Authorization"] = `Bearer ${response.data.token}`;

        // Store user information in session storage
        sessionStorage.setItem("user_id", userId);
        sessionStorage.setItem("name", username);
        sessionStorage.setItem("user_type", user_type);

        // Only allow admin users to navigate
        if (user_type === "admin") {
          navigate("/admin/users");
        } else {
          setFormError("You are not authorized to log in.");
        }
      })
      
      .finally(() => {
        setLoading(false); // Reset loading state
      });
  };

  return (
    <div style={{ height: "100vh", display: "flex", justifyContent: "center", alignItems: "center", background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)" }}>
      <div className="container mt-5" style={{ width: "450px", padding: "40px", backgroundColor: "#ffffff", borderRadius: "15px", boxShadow: "4px 4px 15px rgba(0, 0, 0, 0.2)" }}>
        <form onSubmit={onSubmit}>
          <h2 className="text-center" style={{ fontSize: "30px", color: "#007bff" }}>Admin Login</h2>
          
          {/* Username */}
          <div style={{ marginBottom: "20px" }}>
            <label htmlFor="username" style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}>Username:</label>
            <input
              required
              type="text"
              name="username"
              id="username"
              placeholder="Enter your username"
              className="form-control"
              onChange={onChangeHandler}
            />
          </div>

          {/* Password */}
          <div style={{ marginBottom: "20px" }}>
            <label htmlFor="password" style={{ display: "inline-block", width: "150px", textAlign: "right", marginRight: "10px" }}>Password:</label>
            <input
              required
              type="password"
              name="password"
              id="password"
              placeholder="Enter your password"
              className="form-control"
              onChange={onChangeHandler}
            />
          </div>

          {/* Error message */}
          <p className="text-danger" style={{ fontSize: "14px", marginBottom: "20px" }}>{formError}</p>

          {/* Submit button */}
          <div className="text-center">
            <button className="btn btn-dark" style={{ width: "50%", backgroundColor: "#007bff", border: "none", borderRadius: "8px" }} disabled={loading}>
              {loading ? "Logging in..." : "Login"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
