import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Address = () => {
  const [address, setAddress] = useState("");
  const navigate = useNavigate();

  const handleAddressSubmit = async (e) => {
    e.preventDefault();

    try {
      const userId = sessionStorage.getItem("user_id");
      if (!userId) {
        alert("Please log in to add your address.");
        navigate("/login");
        return;
      }

      // Make API call to save the address
      const response = await axios.post("http://localhost:8000/medical_store_app/add_address/", {
        user_id: userId,
        address: address.trim(),
      });

      if (response.status === 200) {
        alert("Address saved successfully!");
        navigate("/checkout");
      } else {
        alert("Failed to save the address. Please try again.");
      }
    } catch (error) {
      console.error("Error saving address:", error);
      alert("An error occurred while saving your address.");
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Add or Update Billing Address</h1>
      <form style={styles.form} onSubmit={handleAddressSubmit}>
        <label style={styles.label}>
          Billing Address:
          <textarea
            style={styles.textarea}
            placeholder="Enter your billing address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </label>
        <button style={styles.button} type="submit">
          Save Address
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: "40px",
    fontFamily: "Arial, sans-serif",
    textAlign: "center",
  },
  heading: {
    fontSize: "2rem",
    marginBottom: "20px",
  },
  form: {
    maxWidth: "500px",
    margin: "0 auto",
    textAlign: "left",
  },
  label: {
    display: "block",
    marginBottom: "10px",
    fontWeight: "bold",
  },
  textarea: {
    width: "100%",
    height: "150px",
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "5px",
    resize: "none",
  },
  button: {
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    padding: "10px 20px",
    fontSize: "1rem",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "20px",
  },
};

export default Address;
